={{ 
(() => {
  const msg = ( $('WhatsApp Trigger').item.json.messages[0].text.body.toLowerCase().trim().replace(/[^\w\s#]/g, '')|| "").trim();
  const low = msg.toLowerCase();

  // Regex patterns
  const rePriority = /\b(not\s+(?:acceptable|happy|satisfied|working)|angry|frustrat(?:ed|ing)|disappoint(?:ed|ing)|complain(?:t|ing)?|escalate|fed\s*up|ridiculous|worst|cheat(?:ed)?|fake|scam|delay(?:ed)?|late|still\s+haven'?t\s+received|no\s+update|i\s+don'?t\s+believe|unacceptable|3\+?\s*weeks|weeks?\s*(?:now|and\s*no\s*update))\b|[\?\!]{2,}/i;
  const reRefund = /\b(refund(?:ed|s)?|refund\s*status|refund\s*not\s*(?:received|got)|return(?:s|ing)?|exchange|replacement|replace|cancel(?:led|ation)?|cancel\s*(?:my\s*)?order|return\s*policy|refund\s*policy|rto\s*refund|money\s*back)\b/i;
  const reOrder = /\b(where\s+is\s+my\s+order|order\s*status|status\s+of|dispatc?h(?:ed)?|shipp(?:ed|ing)?|delivery|deliver(?:ed)?\s*(?:date|eta)?|track(?:ing)?\s*(?:id|no\.?|number|details)?|awb|courier|out\s*for\s*delivery|when\s+can\s+i\s+expect|eta|tracking\s*id|tracking\s*no\.?|tracking\s*number)\b/i;
  const reProducts = /\b(product(?:s)?|available|availability|catalog|collection|inventory|in\s*stock|restock|size(?:s)?|size\s*chart|color|colour|colors|colours|variant(?:s)?|price|pricing|cost|mrp|new\s*arrivals?|latest)\b/i;
  const reStarter = /^(?:\s*(hi|hello|hey|hola|namaste|yo|sup|greetings|gm|good\s*morning|good\s*afternoon|good\s*evening|gn)\b|üëã)\s*[!.?]*$/i;

  // Priority order
  if (reOrder.test(msg)) return "Order status";
  if (reProducts.test(msg)) return "Products";
  if (reStarter.test(msg) && msg.length < 40) return "Starter";
  return "Support";
})()
}}

