={{
(() => {
  const products = ($node["Products Agent"].json.output || []);

  const lines = products.map(p => {
    // sizes can be an array OR a comma-separated string; normalize to array
    const arr = Array.isArray(p.sizes)
      ? p.sizes
      : (typeof p.sizes === 'string'
          ? p.sizes.split(',').map(s => s.trim()).filter(Boolean)
          : []);

    const sizesText = arr.length ? "Sizes: " + arr.join(", ") : "‚ùå Currently out of stock";
    return "‚ú® " + p.productName + "\n" + sizesText;
  });

  return "üëã Hey there!\nHere are our latest available pieces:\n\n"
    + lines.join("\n\n");
})()
}}
