// Gelen değer array olabilir
const raw = $json.appended_displayName;

// Array olup olmadığını kontrol et, stringe çevir
let output;
if (Array.isArray(raw)) {
  output = raw.join(", ");
} else {
  output = String(raw);
}

return {
  cleanedString: output
};

