// Parse the JSON from content (sometimes it's stringified with markdown formatting)
let parsed;
try {
  const raw = $json.content.trim();
  const jsonText = raw.startsWith('```') ? raw.replace(/```(json)?/g, '').trim() : raw;
  parsed = JSON.parse(jsonText);
} catch (err) {
  throw new Error("Failed to parse 'content' as JSON");
}

const quadrants = parsed.quadrants || {};

function formatTasks(title, tasks) {
  if (!tasks || !tasks.length) return '';
  const lines = tasks.map(t =>
    `- [${t.finished ? 'x' : ' '}] ${t.task}`
  );
  return [`=== ${title} ===`, ...lines].join('\n');
}

const blocks = [
  formatTasks('Urgent & Important', quadrants.urgent_important),
  formatTasks('Not Urgent but Important', quadrants.not_urgent_important),
  formatTasks('Urgent but Not Important', quadrants.urgent_not_important),
  formatTasks('Not Urgent & Not Important', quadrants.not_urgent_not_important),
].filter(Boolean); // remove empty sections

return [{ json: { weeklyplan_joined: blocks.join('\n\n') } }];

