// Run Once for All Items
// Purpose: Safely parse the "content" field coming from the LLM (or other nodes),
// which may be in multiple inconsistent formats:
//   - Already a JSON object
//   - A JSON string (valid)
//   - A JSON string wrapped in ```json ... ``` code fences
//   - A JSON string prefixed with "json\n"
//   - A JSON string that's double-encoded (JSON string inside a string)
//   - Or even invalid, in which case we want to fail gracefully

function toObj(val) {
  if (val == null) return {};                 // Case 1: if content is null/undefined → return empty object
  if (typeof val === 'object') return val;    // Case 2: if it's already parsed into an object → just return it

  // Otherwise, assume it's a string
  let txt = String(val).trim();               // Convert to string & trim whitespace

  // Remove markdown code fences ```json ... ```
  txt = txt.replace(/^```(?:json)?\s*/i, '')  // Remove opening ``` or ```json
           .replace(/```$/,'')                // Remove closing ```
           .trim();

  // Some models add "json\n{...}" before the object → remove the leading "json"
  txt = txt.replace(/^json\s*/i, '');

  // --- Attempt parsing in several ways ---

  // Attempt #1: Normal JSON.parse
  try { 
    return JSON.parse(txt); 
  } catch {}

  // Attempt #2: Double-encoded JSON
  // Example: "\"{ \\\"key\\\":123 }\"" → needs two parses
  try { 
    return JSON.parse(JSON.parse(txt)); 
  } catch {}

  // Attempt #3: Extract first {...} block and try parsing that
  // Useful if there’s extra text around the JSON
  const m = txt.match(/\{[\s\S]*\}$/);   // greedy match from first { to last }
  if (m) { 
    try { return JSON.parse(m[0]); } catch {} 
  }

  // If all parsing attempts fail, return a fallback object
  return { 
    _raw: txt,                         // Keep the raw string so we don't lose information
    _error: 'could_not_parse_json'     // Add a flag for debugging
  };
}

// --- Main execution starts here ---
// $json is the current item in n8n
// We check for the field "content" (or sometimes LLM outputs live under message.content)
const content = $json.content ?? $json.message?.content ?? '';

// Apply the parser to try to normalize content into an object
const obj = toObj(content);

// Return the result as n8n output
// - weeklyplan_joined: always a string version (good for downstream LLM prompts)
// - parsed_weekly: the actual parsed object (good for structured handling)
return [{
  json: {
    weeklyplan_joined: typeof obj === 'string' ? obj : JSON.stringify(obj),
    parsed_weekly: obj
  }
}];

const blocks = $items('Filter2').map((i, idx) => {
  const j = i.json;
  return [
    `=== OKR ${idx + 1} ===`,
    `Objective: ${j.property_objective || '(missing)'}`,
    `Key Result: ${j.property_key_result || '(missing)'}`,
    `Status: ${j.property_status || 'Unknown'}`
  ].join('\n');
});

return [{ json: { okrs_joined: blocks.join('\n\n') } }];

const isNoise = (item) => {
  const labels = item.json.labels?.map(l => l.name.toUpperCase()) || [];
  const subject = (item.json.Subject || '').toLowerCase();

  // Exclude by labels
  const hasBadLabel = labels.some(l =>
    ['CATEGORY_PROMOTIONS', 'CATEGORY_SOCIAL', 'SPAM', 'n8n', 'TRASH'].includes(l)
  );

  // Exclude by subject (receipts)
  const isReceipt = subject.includes('receipt') || subject.includes('receipts');

  return hasBadLabel || isReceipt;
};

// Filter
const filtered = items.filter(item => !isNoise(item));

// Fallback: if empty, return dummy
if (filtered.length === 0) {
  return [{
    json: {
      Subject: '',
      From: '',
      body: 'No human-relevant emails this week.',
    }
  }];
}

return filtered;
