const response = $input.item.json;
let articleData;

try {
  let content = response.message?.content || '';

  // Clean up JSON extraction
  if (content.includes('```json')) {
    const start = content.indexOf('```json') + 7;
    const end = content.lastIndexOf('```');
    content = content.substring(start, end).trim();
  }

  // Handle incomplete JSON
  if (!content.trim().endsWith('}')) {
    const lastQuote = content.lastIndexOf('"');
    if (lastQuote > 0) {
      content = content.substring(0, lastQuote + 1) + '}';
    }
  }

  articleData = JSON.parse(content);

} catch (e) {
  console.log('JSON parse failed:', e.message);

  // Fallback extraction
  const raw = response.message?.content || '';
  articleData = {
    title: (raw.match(/"title"\s*:\s*"([^"]*)"/)?.[1]) || "Industry Update",
    metaDescription: (raw.match(/"metaDescription"\s*:\s*"([^"]*)"/)?.[1]) || "Latest industry news",
    content: (raw.match(/"content"\s*:\s*"([^"]*)"/s)?.[1]) || "Content extraction failed",
    focusKeyphrase: "ai automation",
    readingTime: 8,
    seoScore: 90,
    wordCount: 2500
  };
}

// MINIMAL formatting since GPT should output correctly
let formattedContent = (articleData.content || "")
  // Fix escaped characters
  .replace(/\\"/g, '"')
  .replace(/\\n\\n/g, '\n\n')
  .replace(/\\n/g, '\n')

  // Just clean up any remaining spacing issues
  .replace(/\n{3,}/g, '\n\n')
  .replace(/^\n+|\n+$/g, '')
  .trim();

// Calculate actual word count
const wordCount = formattedContent.split(/\s+/).filter(word => word.length > 0).length;

return [{
  article: {
    title: articleData.title,
    metaDescription: articleData.metaDescription,
    content: formattedContent,
    focusKeyphrase: articleData.focusKeyphrase || "ai automation",
    readingTime: Math.ceil(wordCount / 250) || 8,
    seoScore: articleData.seoScore || 90,
    wordCount: wordCount
  },
  publishing: {
    slug: (articleData.title || "article")
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/^-+|-+$/g, '')
      .substring(0, 60),
    featured: true,
    publishedAt: new Date().toISOString()
  },
  seo: {
    keyword: articleData.focusKeyphrase || "ai automation",
    intent: "informational",
    difficulty: 5,
    volume: "5000/mo",
    contentAngle: "industry analysis"
  },
  metadata: {
    category: "AI & Technology",
    originalUrl: "",
    originalSource: "Generated",
    sources: [],
    tags: ["ai", "enterprise", "automation", "technology"]
  },
  image: {
    url: "https://source.unsplash.com/1200x630/?technology,artificial-intelligence",
    alt: articleData.title || "Technology"
  }
}];

// Parse search results and extract the best article
const searchResults = $input.item.json;
let articles = [];

// Handle Google Custom Search API response
if (searchResults.items && Array.isArray(searchResults.items)) {
 searchResults.items.forEach(item => {
   const urlParts = item.link.match(/^https?:\/\/([^\/]+)/);
   const hostname = urlParts ? urlParts[1] : 'unknown';

   articles.push({
     title: item.title,
     url: item.link,
     snippet: item.snippet,
     source: hostname,
     publishDate: new Date().toISOString()
   });
 });
}

// Return all articles found
return articles;

