const articles = $input.all();
const filteredArticles = [];

for (const article of articles) {
  const url = article.json.url || '';

  // Skip domains known to have strong anti-bot protection
  const blockedDomains = [
    'aibusiness.com',
    'forbes.com', 
    'wsj.com',
    'ft.com'
  ];

  const isBlocked = blockedDomains.some(domain => url.includes(domain));

  if (!isBlocked) {
    filteredArticles.push(article.json);
  }
}

// If all articles are blocked, use the first one with just the snippet
if (filteredArticles.length === 0 && articles.length > 0) {
  const fallback = articles[0].json;
  fallback.content = fallback.snippet;
  filteredArticles.push(fallback);
}

return filteredArticles;
