/**
 * N8N'den gelen Gemini response'unu parse eder ve JSON'a çevirir.
 * Birden fazla strateji kullanarak güvenilir parsing sağlar.
 *
 * @param {string} geminiResponse - N8N'den gelen ham Gemini yanıtı
 * @returns {object|null} Parse edilmiş JSON objesi veya null
 */
function parseGeminiResponse(geminiResponse) {
    // Giriş kontrolü
    if (!geminiResponse || typeof geminiResponse !== 'string') {
        console.error("Boş veya geçersiz Gemini response'u");
        return null;
    }

    console.log(`Parsing Gemini response, length: ${geminiResponse.length} characters`);

    // Response'u temizle
    const cleanedResponse = cleanGeminiResponse(geminiResponse);

    if (!cleanedResponse.trim()) {
        console.error("Temizlendikten sonra boş response");
        return null;
    }

    // JSON parse etme stratejileri
    const parseStrategies = [
        directJsonParse,           // Direkt parse
        extractFirstJsonObject,   // İlk JSON nesnesini bul
        fixAndParseJson,         // Yaygın sorunları düzelt
        regexExtractJson         // Regex ile JSON bul
    ];

    for (let i = 0; i < parseStrategies.length; i++) {
        try {
            const result = parseStrategies[i](cleanedResponse);
            if (result && typeof result === 'object') {
                console.log(`JSON başarıyla parse edildi (strateji ${i + 1})`);

                // Blog yapısını validate et
                if (validateBlogStructure(result)) {
                    console.log("Geçerli blog JSON yapısı bulundu");
                } else {
                    console.warn("Blog JSON yapısı eksik alanlar içeriyor");
                }

                return result;
            }
        } catch (error) {
            console.log(`Parse stratejisi ${i + 1} başarısız: ${error.message}`);
            continue;
        }
    }

    // Tüm stratejiler başarısız
    const responsePreview = cleanedResponse.length > 500 
        ? cleanedResponse.substring(0, 500) + "..." 
        : cleanedResponse;

    console.error(
        `Gemini response hiçbir strateji ile parse edilemedi. ` +
        `Response uzunluğu: ${cleanedResponse.length} karakter. ` +
        `Response önizlemesi: ${responsePreview}`
    );

    return null;
}

/**
 * Gemini response'unu temizler - markdown, extra whitespace vs.
 */
function cleanGeminiResponse(response) {
    // Markdown code block'larını kaldır
    response = response.replace(/```json\s*/gi, '');
    response = response.replace(/```\s*/g, '');

    // Başlangıç ve son whitespace'leri temizle
    response = response.trim();

    // Eğer multiple JSON objesi varsa sadece ilkini al
    if ((response.match(/\{/g) || []).length > 1) {
        response = extractFirstCompleteJson(response);
    }

    return response;
}

/**
 * İlk tam JSON nesnesini çıkarır
 */
function extractFirstCompleteJson(text) {
    const firstBrace = text.indexOf('{');
    if (firstBrace === -1) {
        return text;
    }

    let braceCount = 0;
    for (let i = firstBrace; i < text.length; i++) {
        if (text[i] === '{') {
            braceCount++;
        } else if (text[i] === '}') {
            braceCount--;
            if (braceCount === 0) {
                return text.substring(firstBrace, i + 1);
            }
        }
    }

    return text; // Tam JSON bulunamazsa orijinali döndür
}

/**
 * Direkt JSON parse
 */
function directJsonParse(text) {
    return JSON.parse(text);
}

/**
 * İlk geçerli JSON nesnesini bulur
 */
function extractFirstJsonObject(text) {
    const firstBrace = text.indexOf('{');
    if (firstBrace === -1) {
        throw new Error("JSON objesi bulunamadı");
    }

    let braceCount = 0;
    for (let i = firstBrace; i < text.length; i++) {
        if (text[i] === '{') {
            braceCount++;
        } else if (text[i] === '}') {
            braceCount--;
            if (braceCount === 0) {
                const jsonText = text.substring(firstBrace, i + 1);
                return JSON.parse(jsonText);
            }
        }
    }

    throw new Error("Tamamlanmamış JSON objesi");
}

/**
 * Yaygın JSON sorunlarını düzeltir
 */
function fixAndParseJson(text) {
    // Eksik kapanış parantezlerini tamamla
    const openBraces = (text.match(/\{/g) || []).length;
    const closeBraces = (text.match(/\}/g) || []).length;

    if (openBraces > closeBraces) {
        text += '}'.repeat(openBraces - closeBraces);
    }

    // Trailing comma'ları kaldır
    text = text.replace(/,\s*}/g, '}');
    text = text.replace(/,\s*]/g, ']');

    return JSON.parse(text);
}

/**
 * Regex ile JSON bulur
 */
function regexExtractJson(text) {
    // Nested JSON pattern
    const jsonPattern = /\{(?:[^{}]|(?:\{(?:[^{}]|(?:\{[^{}]*\})*)*\})*)*\}/g;
    const matches = text.match(jsonPattern);

    if (!matches || matches.length === 0) {
        throw new Error("Regex ile JSON pattern bulunamadı");
    }

    // En uzun match'i al
    const longestMatch = matches.reduce((a, b) => a.length > b.length ? a : b);
    return JSON.parse(longestMatch);
}

/**
 * Blog JSON yapısının gerekli alanları içerip içermediğini kontrol eder
 */
function validateBlogStructure(data) {
    if (!data || typeof data !== 'object') {
        return false;
    }

    // Temel gerekli alanlar
    const requiredFields = ['title', 'description', 'slug', 'body'];

    const missingFields = [];
    for (const field of requiredFields) {
        if (!data[field]) {
            missingFields.push(field);
        }
    }

    if (missingFields.length > 0) {
        console.warn(`Eksik gerekli alanlar: ${missingFields.join(', ')}`);
        return false;
    }

    // SEO alanlarını kontrol et (opsiyonel ama önemli)
    if (data.seo && typeof data.seo === 'object') {
        const seoFields = ['metaTitle', 'metaDescription'];
        const missingSeo = seoFields.filter(field => !data.seo[field]);
        if (missingSeo.length > 0) {
            console.warn(`Eksik SEO alanları: ${missingSeo.join(', ')}`);
        }
    }

    return true;
}

/**
 * Parse edilmiş JSON'dan blog içeriğini çıkarır ve düzenler
 */
function extractBlogContent(data) {
    if (!data || typeof data !== 'object') {
        throw new Error("Geçersiz data formatı");
    }

    // Blog içeriğini çıkar
    const blogContent = {
        title: data.title || '',
        description: data.description || '',
        slug: data.slug || '',
        body: data.body || '',
        author: data.author || 'AI Content Generator',
        category: data.category || 'Genel',
        image_generation_prompt: data.image_generation_prompt || '',
        seo: data.seo || {},
        created_at: '2025-09-03 07:48:33', // Current timestamp
        created_by: '0Baris', // Current user
        status: 'draft'
    };

    // SEO alanlarını düzenle
    if (!blogContent.seo || Object.keys(blogContent.seo).length === 0) {
        blogContent.seo = {
            metaTitle: blogContent.title.length > 60 
                ? blogContent.title.substring(0, 60) + '...' 
                : blogContent.title,
            metaDescription: blogContent.description.length > 160 
                ? blogContent.description.substring(0, 160) + '...' 
                : blogContent.description
        };
    }

    // Body'den blog content comment'ini temizle
    if (blogContent.body) {
        blogContent.body = blogContent.body
            .replace(/<!-- BLOG CONTENT \(MARKDOWN\) -->\s*/g, '')
            .trim();
    }

    return blogContent;
}

/**
 * N8N'den gelen Gemini response'unu işler ve blog için hazır hale getirir
 */
function processN8nGeminiResponse(geminiResponse) {
    try {
        // Response'u parse et
        const parsedData = parseGeminiResponse(geminiResponse);

        if (!parsedData) {
            throw new Error("Gemini response parse edilemedi");
        }

        // Blog içeriğini çıkar ve düzenle
        const blogContent = extractBlogContent(parsedData);

        console.log(`Blog içeriği başarıyla işlendi: ${blogContent.title}`);
        return blogContent;

    } catch (error) {
        console.error(`Gemini response işlenirken hata: ${error.message}`);
        return null;
    }
}

// --- N8N KULLANIMI ---

// Gemini'den gelen yanıtın n8n'deki yolu
// Mevcut kodunuzdaki gibi
const geminiResponseText = $input.first().json.content.parts[0].text;

// Geliştirilmiş fonksiyonu çağır
const cleanedData = processN8nGeminiResponse(geminiResponseText);

// Eğer işlem başarısız olursa, iş akışının bu kolunun devam etmesini engelle
if (cleanedData === null) {
    console.error("JSON temizleme ve ayrıştırma işlemi başarısız oldu. İş akışı bu koldan devam etmeyecek.");
    return []; // Boş dizi, n8n'in bu yolu sonlandırmasını sağlar
}

// Başarılı olursa, temizlenmiş veriyi bir sonraki düğüme gönder
return {
    json: cleanedData
};
