const views = Number($json.ViewCount || 0);
const likes = Number($json.likeCount || 0);
const comments = Number($json.commentCount || 0);

const publishedAt = $json.publishedAt || '';
const publishTime = new Date (publishedAt).getTime();
const now = Date.now();
const hoursSincePublish = (now - publishTime) / (1000 * 60 * 60); // Convert millisecond

const likeRate = views > 0 ? (likes / views) * 100 : 0; // Like percentage 
const commentRate = views > 0 ? (comments / views) * 100 : 0; // Comment percentage 
const viewsPerHour = hoursSincePublish > 0 ? views / hoursSincePublish: 0; // Views per

const title = $json.title || '';
const videoID = $json.videoId || '';
