const output = [];
const currentTimestamp = Date.now(); // Get current timestamp in milliseconds

for (const item of items) {

  const videoData = item.json;

  const channelId = videoData.channelId; 
  const subscriberCount = Number(videoData.subscriberCount || 0);
  const videoViews = Number(videoData['views'] || 0); 
  const numberOfLikes = Number(videoData['likes'] || 0); 
  const numberOfComments = Number(videoData['comments'] || 0);
  const videoPublishedAt = videoData.publishedAt; 


  // --- 1. Calculate hoursSincePublished ---
  let hoursSincePublished;
  try {
    const publishedDate = new Date(videoPublishedAt);
    if (isNaN(publishedDate.getTime())) {
      throw new Error(`Invalid videoPublishedAt date: "${videoPublishedAt}"`);
    }
    hoursSincePublished = (currentTimestamp - publishedDate.getTime()) / (1000 * 60 * 60);

    hoursSincePublished = Math.max(1, hoursSincePublished);
  } catch (e) {

    hoursSincePublished = 1; // Default to 1 hour if date is invalid or missing
  }


  // --- 2. Calculate viewsPerHour ---
  const viewsPerHour = videoViews / hoursSincePublished;

  // --- 3. Handle effectiveSubscriberCount ---
  const effectiveSubscriberCount = Math.max(1, subscriberCount); // Avoid division by zero

  // --- 4. Calculate Views_Per_Subscriber ---
  const viewsPerSubscriber = videoViews / effectiveSubscriberCount;

  // --- 5. Calculate Relative_Engagement_Rate ---
  let likeRate = 0;
  let commentRate = 0;
  if (videoViews > 0) { // Only calculate rates if there are views to avoid division by zero
    likeRate = numberOfLikes / videoViews;
    commentRate = numberOfComments / videoViews;
  }
  const relativeEngagementRate = (likeRate + commentRate) * 100;


  // --- 6. Calculate Log_Views_Per_Hour (for Normalized_Views_Per_Hour) ---
  const logViewsPerHour = Math.log10(viewsPerHour + 1); // Add 1 to handle 0 gracefully


  // --- 7. Calculate Algorithmic_Lift_Score (ALS) ---
  const W1 = 0.6; // Weight for Views_Per_Subscriber
  const W2 = 0.3; // Weight for Relative_Engagement_Rate
  const W3 = 0.1; // Weight for Log_Views_Per_Hour

  const algorithmicLiftScore =
    (W1 * viewsPerSubscriber) +
    (W2 * relativeEngagementRate) +
    (W3 * logViewsPerHour);

  // Add the calculated score and other relevant data to the output
  output.push({
    json: {
      ...videoData, // Keep all original data
      algorithmicLiftScore: algorithmicLiftScore,
      relativeEngagementRate: relativeEngagementRate,
      logViewsPerHour: logViewsPerHour,
      hoursSincePublished: hoursSincePublished,
      viewsPerHour: viewsPerHour,
      viewsPerSubscriber: viewsPerSubscriber,
      likeRate: likeRate,
      commentRate: commentRate
    },
  });
}

return output;
