// n8n Code node: Categorize & Sum Time Entries

// 1) Grab the array of users
const users = $input.first().json.time_entries || [];

// 2) Define keyword lists & precedence
const categories = [
  { name: 'Development', keywords: ['development','dev','feature','implement','coding','build'] },
  { name: 'Commenting+Call+Documentation', 
    keywords: ['comment','comments','call','calls','documentation','docs','doc'] },
  { name: 'Miscellaneous', keywords: [] },  // fallback
  { name: 'PR Review',   keywords: ['review','pr','merge','approve'] },
  { name: 'QA',          keywords: ['qa','test','bug','verify','validate'] },
  { name: 'Scoping',     keywords: ['scope','estimation','plan','analy'] },
];

// Helper to find category
function categorizeInterval(interval) {
  const text = [
    interval.description || '',
    ...(interval.tags || [])
  ].join(' | ').toLowerCase();

  for (let cat of categories.slice(0, -1)) {
    if (cat.keywords.some(k => text.includes(k))) {
      return cat.name;
    }
  }
  // No match → fallback
  return 'Miscellaneous';
}

// Helper to format minutes as Xh Ym
function formatMinutes(mins) {
  const h = Math.floor(mins / 60);
  const m = mins % 60;
  return `${h}h ${m}m`;
}

// 3) Process each user
const result = users.map(userObj => {
  const sums = {};
  // Initialize sums
  for (let cat of categories) sums[cat.name] = 0;

  // Categorize & accumulate
  for (let interval of userObj.intervals || []) {
    const cat = categorizeInterval(interval);
    sums[cat] += interval.time_in_minutes || 0;
  }

  // Build output array, omitting zeros
  const time_spent_by_category = Object.entries(sums)
    .filter(([, total]) => total > 0)
    .map(([category, total]) => ({
      category,
      time: formatMinutes(total)
    }));

  return {
    user: userObj.user.username,
    time_spent_by_category
  };
});

// 4) Return as JSON
return {
   result
};
