// n8n Code (v2): Better "Format Email Content"
// - Auto-detects fiat vs crypto inputs (works regardless of Merge order)
// - Defensive parsing + nice HTML + text fallback
// - Uses Africa/Lagos by default for display time (change tz if needed)

const TZ = 'Africa/Lagos';
const NOW = new Date();

const items = $input.all(); // merged inputs
if (!items || items.length < 2) {
  throw new Error('Expected 2 inputs (fiat + crypto), but received ' + (items ? items.length : 0));
}

// Identify which payload is which regardless of order
let fiatData, cryptoData;
for (const it of items) {
  const j = it.json || {};
  if (j.rates && j.base && j.date) fiatData = j;
  if (j.bitcoin || j.ethereum) cryptoData = j;
}

if (!fiatData) {
  throw new Error('Fiat data not found. Ensure the ExchangeRate-API node is connected to the Merge before this Code node.');
}
if (!cryptoData) {
  throw new Error('Crypto data not found. Ensure the CoinGecko node is connected to the Merge before this Code node.');
}

// Extract fiat
const eurRate = Number(fiatData?.rates?.EUR);
const ngnRate = Number(fiatData?.rates?.NGN);
const lastUpdated = String(fiatData?.date || 'N/A');

// Extract crypto
const btcPrice  = Number(cryptoData?.bitcoin?.usd);
const btcChange = Number(cryptoData?.bitcoin?.usd_24h_change);
const ethPrice  = Number(cryptoData?.ethereum?.usd);
const ethChange = Number(cryptoData?.ethereum?.usd_24h_change);

// Helpers
const fmtNum = (num, decimals = 2) =>
  isFinite(num) ? new Intl.NumberFormat('en-US', {
    minimumFractionDigits: decimals, maximumFractionDigits: decimals
  }).format(num) : '‚Äî';

const arrow = (n) => (isFinite(n) ? (n >= 0 ? '‚ñ≤' : '‚ñº') : '');
const color = (n) => (isFinite(n) ? (n >= 0 ? '#0FA958' : '#E03E2F') : '#6B7280');
const sign  = (n) => (isFinite(n) && n > 0 ? '+' : '');

const fmtPct = (n) =>
  isFinite(n)
    ? `<span style="color:${color(n)};font-weight:600">${sign(n)}${fmtNum(n, 2)}%</span> ${arrow(n)}`
    : '<span style="color:#6B7280">‚Äî</span>';

const fmtDate = (d=NOW, tz=TZ) =>
  d.toLocaleString('en-US', { timeZone: tz, weekday:'long', year:'numeric', month:'long', day:'numeric', hour:'2-digit', minute:'2-digit' });

// Build HTML
const html = `
<!doctype html>
<html>
<head>
<meta charset="utf-8">
<meta name="viewport" content="width=device-width">
<title>Daily Currency Report</title>
</head>
<body style="margin:0;padding:24px;background:#F5F7FB;font-family:Inter,system-ui,Segoe UI,Roboto,Arial,sans-serif;">
  <div style="max-width:640px;margin:0 auto;background:#ffffff;border-radius:12px;box-shadow:0 8px 24px rgba(16,24,40,0.08);overflow:hidden;">
    <div style="background:linear-gradient(135deg,#667EEA 0%,#764BA2 100%);padding:28px 24px;text-align:center;">
      <div style="font-size:28px;color:#fff;letter-spacing:0.2px">üí∞ Daily Currency Report</div>
      <div style="margin-top:6px;color:rgba(255,255,255,0.9);font-size:14px">${fmtDate()}</div>
    </div>

    <div style="padding:24px">
      <h2 style="margin:0 0 12px 0;font-size:18px;color:#111827">üåç Fiat Exchange Rates (USD base)</h2>
      <div style="display:grid;gap:12px">
        <div style="background:#F8FAFC;border-left:4px solid #3B82F6;padding:16px;border-radius:8px">
          <div style="display:flex;justify-content:space-between;align-items:center">
            <div>
              <div style="font-weight:600;color:#111827">üá∫üá∏ USD ‚Üí üá™üá∫ EUR</div>
              <div style="color:#6B7280;font-size:12px">US Dollar to Euro</div>
            </div>
            <div style="font-weight:700;color:#1D4ED8;font-size:20px">${fmtNum(eurRate, 4)}</div>
          </div>
        </div>

        <div style="background:#F8FAFC;border-left:4px solid #10B981;padding:16px;border-radius:8px">
          <div style="display:flex;justify-content:space-between;align-items:center">
            <div>
              <div style="font-weight:600;color:#111827">üá∫üá∏ USD ‚Üí üá≥üá¨ NGN</div>
              <div style="color:#6B7280;font-size:12px">US Dollar to Nigerian Naira</div>
            </div>
            <div style="font-weight:700;color:#065F46;font-size:20px">‚Ç¶${fmtNum(ngnRate, 2)}</div>
          </div>
        </div>
      </div>

      <h2 style="margin:24px 0 12px 0;font-size:18px;color:#111827">‚Çø Cryptocurrency Prices</h2>
      <div style="display:grid;gap:12px">
        <div style="background:#FFFBEB;border-left:4px solid #F59E0B;padding:16px;border-radius:8px">
          <div style="display:flex;justify-content:space-between;align-items:center">
            <div>
              <div style="font-weight:600;color:#111827">‚Çø Bitcoin (BTC)</div>
              <div style="color:#6B7280;font-size:12px">24h change: ${fmtPct(btcChange)}</div>
            </div>
            <div style="font-weight:700;color:#B45309;font-size:20px">$${fmtNum(btcPrice, 0)}</div>
          </div>
        </div>

        <div style="background:#EEF2FF;border-left:4px solid #6366F1;padding:16px;border-radius:8px">
          <div style="display:flex;justify-content:space-between;align-items:center">
            <div>
              <div style="font-weight:600;color:#111827">‚ü† Ethereum (ETH)</div>
              <div style="color:#6B7280;font-size:12px">24h change: ${fmtPct(ethChange)}</div>
            </div>
            <div style="font-weight:700;color:#3730A3;font-size:20px">$${fmtNum(ethPrice, 0)}</div>
          </div>
        </div>
      </div>

      <div style="margin-top:20px;color:#6B7280;font-size:12px;border-top:1px solid #E5E7EB;padding-top:12px">
        üìä Fiat updated: ${lastUpdated} &nbsp;|&nbsp; ‚è±Ô∏è Generated: ${fmtDate()} (${TZ})
      </div>
    </div>

    <div style="background:#F9FAFB;padding:14px;text-align:center;color:#6B7280;font-size:12px;border-top:1px solid #E5E7EB">
      This email was generated automatically by your n8n currency tracker.
    </div>
  </div>
</body>
</html>
`;

// Plain text
const text = [
  `Daily Currency Report ‚Äî ${NOW.toLocaleDateString('en-US', { timeZone: TZ })}`,
  ``,
  `Fiat (USD base)`,
  `‚Ä¢ USD ‚Üí EUR: ${fmtNum(eurRate, 4)}`,
  `‚Ä¢ USD ‚Üí NGN: ‚Ç¶${fmtNum(ngnRate, 2)}`,
  ``,
  `Crypto`,
  `‚Ä¢ BTC: $${fmtNum(btcPrice, 0)} (24h: ${sign(btcChange)}${fmtNum(btcChange, 2)}%)`,
  `‚Ä¢ ETH: $${fmtNum(ethPrice, 0)} (24h: ${sign(ethChange)}${fmtNum(ethChange, 2)}%)`,
  ``,
  `Fiat updated: ${lastUpdated} | Generated: ${fmtDate()} (${TZ})`
].join('\n');

return [
  {
    json: {
      subject: `üí∞ Daily Currency Report ‚Äî ${NOW.toLocaleDateString('en-US', { timeZone: TZ })}`,
      html,
      text,
      summary: {
        usd_eur: eurRate,
        usd_ngn: ngnRate,
        btc_usd: btcPrice,
        btc_change_24h: btcChange,
        eth_usd: ethPrice,
        eth_change_24h: ethChange,
        fiat_date: lastUpdated,
        generated_at: fmtDate()
      }
    }
  }
];
