={{
  // Convert the start_time field into a JavaScript Date
  // Get the time in milliseconds since Unix epoch
  // Divide by 1000 to convert to seconds
  new Date($json.start_time).getTime() / 1000
}}

={{ 
// Reduce the occurrences array to find the next occurrence
new Date(
    $json.occurrences.reduce((closest, current) => {
      const currentDate = new Date(current.start_time);
      // Check if current occurrence is closer and in the future
      return (!closest || currentDate < new Date(closest.start_time) && currentDate > new Date()) ? current : closest;
    }, null).start_time // Get the start_time of the closest occurrence
  ).getTime() / 1000 // Convert to Unix timestamp in seconds
}}
