// 1) Read POST body (supports Webhook { body: {...} } or raw JSON)
const src = items?.[0]?.json ?? {};
const bodyRaw = (src.body ?? src ?? {});
const body = (typeof bodyRaw === 'string')
  ? (() => { try { return JSON.parse(bodyRaw); } catch { return {}; } })()
  : bodyRaw;

// 2) Helpers
function normalizeDomain(input = '') {
  const s = String(input).trim();
  if (!s) return '';
  return s
    .replace(/^https?:\/\//i, '') // remove protocol
    .replace(/^www\./i, '')       // remove www
    .replace(/\/+$/g, '');        // remove trailing slashes
}
function splitList(input) {
  if (Array.isArray(input)) return input.map(String);
  return String(input || '')
    .split(/[,\n\r;]+/)
    .map(s => s.trim())
    .filter(Boolean);
}
function toGlobs(list) {
  return splitList(list).map(p => (p.endsWith('*') ? p : `${p}*`));
}

// 3) Map incoming fields
const row = {
  client_name:   body.client_name ?? body.name ?? '',
  client_domain: body.client_domain ?? '',
  competitors:   body.competitors ?? body.competitor ?? body.url ?? '',
  include_paths: body.include_paths ?? '',
  exclude_paths: body.exclude_paths ?? '',
  max_pages:     body.max_pages ?? '',
  crawl_depth:   body.crawl_depth ?? '',
  notify_email:  body.notify_email ?? body.email ?? '',
  processed:     (typeof body.processed === 'boolean'
                    ? body.processed
                    : String(body.processed ?? '').toLowerCase() === 'true'),
  rowIndex:      body.rowIndex ?? null,
};

// 4) Early exit if sender marked it processed
if (row.processed === true) return [];

// 5) Build Apify fields
const competitorDomain = normalizeDomain(row.competitors);
const clientDomain     = normalizeDomain(row.client_domain);

const startUrls = competitorDomain
  ? [{ url: `https://${competitorDomain}/`, method: 'GET' }]
  : [];

if (!startUrls.length) {
  return [{ json: { error: 'NO_START_URLS', raw_competitors: row.competitors || '' } }];
}

const includeArr = toGlobs(row.include_paths);
const excludeArr = toGlobs(row.exclude_paths);

const maxPages   = Number(row.max_pages)   || 20;
const crawlDepth = Number(row.crawl_depth) || 1;

// 6) Output for next nodes
return [{
  json: {
    client_name:       String(row.client_name || ''),
    client_domain:     clientDomain,
    competitor_domain: competitorDomain,
    startUrls,
    includeArr,
    excludeArr,
    max_pages_num:     maxPages,
    crawl_depth_num:   crawlDepth,
    notify_email:      String(row.notify_email || ''),
    rowIndex:          row.rowIndex,
  }
}];

// Renamed variables for clarity; behavior unchanged.
const sourceItem = items?.[0]?.json;
if (!sourceItem) return [];

const markdownText = String(sourceItem.markdown || sourceItem.text || '');
const wordCount = markdownText.trim().split(/\s+/).filter(Boolean).length;

function getTitleFromMarkdown(markdown, url) {
  const lines = markdown.split(/\r?\n/).map(line => line.trim());

  const h1Line = lines.find(line => /^#\s+/.test(line));
  if (h1Line) return h1Line.replace(/^#\s+/, '').trim();

  const firstMeaningfulLine = lines.find(line => line.length > 0 && line.length < 120);
  if (firstMeaningfulLine) return firstMeaningfulLine;

  try {
    const parsedUrl = new URL(url);
    const pathSegments = parsedUrl.pathname.split('/').filter(Boolean);
    return (pathSegments.pop() || parsedUrl.hostname).replace(/[-_]/g, ' ');
  } catch {
    return url;
  }
}

return [{
  json: {
    page_url: sourceItem.url,
    title: getTitleFromMarkdown(markdownText, sourceItem.url),
    word_count: wordCount,
    reading_time_min: Math.max(1, Math.ceil(wordCount / 200)),
    excerpt: markdownText.slice(0, 600),
    markdown: markdownText
  }
}];

function stripCodeFences(text = "") {

  return String(text)
    .replace(/^\s*```(?:json)?\s*/i, "")
    .replace(/\s*```\s*$/i, "")
    .trim();
}

function safeParseModelJson(payload) {

  if (payload && (payload.page_url || payload.main_topics || payload.key_entities)) return payload;

  let raw = payload?.output ?? payload;
  if (typeof raw !== "string") raw = JSON.stringify(raw ?? {});
  raw = stripCodeFences(raw);

  let obj;
  try { obj = JSON.parse(raw); } catch {}


  if (Array.isArray(obj)) {
    if (obj[0]?.output) {
      try { obj = JSON.parse(stripCodeFences(obj[0].output)); } catch { obj = obj[0] || {}; }
    } else {
      obj = obj[0] || {};
    }
  } else if (obj && typeof obj === "object" && typeof obj.output === "string") {
    try { obj = JSON.parse(stripCodeFences(obj.output)); } catch {}
  }

  return obj || {};
}

function normalizeTopicTree(nodes = [], level = 1) {
  return nodes.map(node => ({
    topic: String(node?.topic ?? ""),
    level: Number(node?.level) || level,
    subtopics: normalizeTopicTree(node?.subtopics || [], (Number(node?.level) || level) + 1),
  }));
}

function toTopicTree(modelObj) {
  let t = modelObj.main_topics;
  if (typeof t === "string") {
    try { t = JSON.parse(t); } catch { t = []; }
  }
  if (!Array.isArray(t)) t = [];
  return normalizeTopicTree(t, 1);
}

function toBulletLines(tree, indent = "") {
  const lines = [];
  for (const node of tree) {
    const text = String(node.topic || "").trim();
    if (text) lines.push(`${indent}- ${text}`);
    if (node.subtopics?.length) lines.push(...toBulletLines(node.subtopics, indent + "  "));
  }
  return lines;
}

function parseEntitiesToBullets(value) {
  let arr = value;

  if (typeof arr === "string") {
    const s = arr.trim();
    if (/^- /m.test(s)) {
      // Already looks like "- item" lines
      arr = s.split(/\r?\n/).map(l => l.replace(/^\s*-\s*/, "").trim()).filter(Boolean);
    } else {
      try { arr = JSON.parse(s); }
      catch { arr = s.split(",").map(x => x.trim()).filter(Boolean); }
    }
  }

  if (!Array.isArray(arr)) arr = [];
  return arr.length ? arr.map(e => `- ${String(e)}`).join("\n") : "- (none)";
}

function chooseUrl(...candidates) {
  for (const c of candidates) {
    const v = String(c ?? "").trim();
    if (v && !/^n\/?a$/i.test(v)) return v; // reject "N/A", "n/a"
  }
  return "";
}

// ---------- main ----------
const inputPayload = items?.[0]?.json ?? {};
const modelObj     = safeParseModelJson(inputPayload);

// Build bullets
const topicTree        = toTopicTree(modelObj);
const mainTopicsFlat   = topicTree.length ? toBulletLines(topicTree).join("\n") : "- (none)";
const keyEntitiesFlat  = parseEntitiesToBullets(modelObj.key_entities);

// Prefer the true URL from "Extract Page Metadata"
let urlFromMeta;
try {
  urlFromMeta =
    $items("Extract Page Metadata", 0, 0)?.json?.page_url ||
    $items("Extract Page Metadata", 0, 0)?.json?.url;
} catch {}

const pageUrl = chooseUrl(
  urlFromMeta,
  inputPayload?.page_url,
  modelObj?.page_url
);

// Output
return [{
  json: {
    page_url: pageUrl,
    main_topics_flat: mainTopicsFlat,
    key_entities_flat: keyEntitiesFlat,
  }
}];

function toSheetName(raw) {
  let s = String(raw || '').trim();
  if (!s) return 'Page';
  if (!/^https?:\/\//i.test(s)) s = 'https://' + s;

  try {
    const u = new URL(s);
    const host = u.hostname.replace(/^www\./, '');
    let path = (u.pathname || '/')
      .replace(/\/+/g, '/')
      .replace(/^\/|\/$/g, '');
    let name = path ? `${host}_${path}` : host;
    name = name.replace(/[:\\\/\?\*\[\]]/g, '¬∑').replace(/\s+/g, ' ').trim();
    return name || 'Page';
  } catch {
    let name = s.replace(/^https?:\/\//i, '').replace(/[:\\\/\?\*\[\]]/g, '¬∑').trim();
    return name || 'Page';
  }
}

// Make N random digits as a string (e.g., "48291")
function randDigits(n = 5) {
  let out = '';
  for (let i = 0; i < n; i++) out += Math.floor(Math.random() * 10);
  return out;
}

const MAX_LEN = 90;        // Sheets tab name limit
const DIGITS = 5;          // how many random digits to append

return items.map(({ json }) => {
  const url = json.page_url ?? json.url ?? json.pageUrl ?? '';
  const base = toSheetName(url);

  const suffix = '_' + randDigits(DIGITS);          
  const roomForBase = Math.max(0, MAX_LEN - suffix.length);
  const safeBase = base.length > roomForBase ? base.slice(0, roomForBase) : base;

  const sheet_name = `${safeBase}${suffix}`;

  return {
    json: {
      ...json,
      sheet_name,
      _sheet_name_src: url 
    }
  };
});

