=<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <title>Competitor Crawl Request</title>
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <style>
    :root{
      --bg-0:#0b1220;
      --bg-1:#0f172a;
      --bg-2:#111827;
      --card:#0b1220;
      --text:#e5e7eb;
      --muted:#94a3b8;
      --line:#1f2937;
      --accent:#7c3aed;      /* violet */
      --accent-2:#06b6d4;    /* cyan */
      --ok:#10b981;          /* green */
      --err:#ef4444;         /* red */
    }
    *{box-sizing:border-box}
    html,body{height:100%}
    body{
      margin:0;
      background:
        radial-gradient(1200px 600px at 20% -10%, rgba(124,58,237,.18), transparent 60%),
        radial-gradient(1000px 400px at 120% 10%, rgba(6,182,212,.20), transparent 60%),
        linear-gradient(180deg, #0b1220, #0b1220 60%, #0a1020 100%);
      color:var(--text);
      font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Arial, "Noto Sans", "Apple Color Emoji","Segoe UI Emoji";
      display:flex; align-items:center; justify-content:center;
      padding:24px;
    }
    .card{
      width:100%;
      max-width: 720px;
      background: linear-gradient(180deg, rgba(255,255,255,.03), rgba(255,255,255,.01));
      border:1px solid var(--line);
      border-radius: 16px;
      box-shadow: 0 10px 30px rgba(0,0,0,.45), inset 0 1px 0 rgba(255,255,255,.03);
      backdrop-filter: blur(8px);
      overflow: hidden;
    }
    .card header{
      padding:20px 24px;
      border-bottom:1px solid var(--line);
      display:flex; align-items:center; gap:12px;
      background: linear-gradient(180deg, rgba(124,58,237,.15), rgba(124,58,237,.02));
    }
    .logo{
      width:36px; height:36px; border-radius:10px;
      background: conic-gradient(from 210deg, var(--accent), var(--accent-2));
      box-shadow: 0 0 0 2px rgba(255,255,255,.06), 0 8px 16px rgba(124,58,237,.2);
    }
    h1{font-size:1.15rem; margin:0}
    .sub{font-size:.9rem; color:var(--muted); margin-top:2px}
    form{padding:20px 24px 24px}
    .grid{
      display:grid;
      grid-template-columns: 1fr;
      gap:16px;
    }
    @media (min-width: 720px){
      .grid{ grid-template-columns: 1fr 1fr; }
      .grid .full{ grid-column: 1 / -1; }
    }
    label{
      display:block; font-size:.92rem; font-weight:600; margin-bottom:8px; color:#c7d2fe;
    }
    input, select, textarea{
      width:100%;
      background:#0d1426;
      color:var(--text);
      border:1px solid #1e293b;
      border-radius:10px;
      padding:12px 12px;
      font-size: 0.98rem;
      outline:none;
      transition: border .15s, box-shadow .15s, transform .02s;
    }
    input::placeholder, textarea::placeholder{ color:#6b7280 }
    input:focus, select:focus, textarea:focus{
      border-color: var(--accent);
      box-shadow: 0 0 0 3px rgba(124,58,237,.2);
    }
    select[multiple]{
      min-height:132px;
      padding:10px;
    }
    small.help{ display:block; color:var(--muted); margin-top:6px; }
    .row{
      display:flex; gap:10px; align-items:center; margin-top:6px;
    }
    .row input{ flex:1 }
    .pill{
      display:inline-flex; align-items:center; gap:8px;
      padding:10px 14px; border-radius:999px;
      border:1px solid #233046; background:#0e1629;
      font-weight:700; letter-spacing:.2px;
      color:#e9d5ff;
      cursor:pointer; user-select:none;
      transition: transform .02s, background .15s, border .15s;
    }
    .pill:hover{ background:#121b31; border-color:#2a3a57 }
    .submit{
      display:flex; gap:12px; align-items:center; justify-content:flex-end;
      padding:18px 24px; border-top:1px solid var(--line);
      background: linear-gradient(180deg, rgba(6,182,212,.08), rgba(6,182,212,.02));
    }
    button{
      all:unset;
      background: linear-gradient(90deg, var(--accent), var(--accent-2));
      padding:12px 18px; border-radius:12px; cursor:pointer; font-weight:700;
      box-shadow: 0 10px 22px rgba(124,58,237,.25);
    }
    button[disabled]{ opacity:.7; cursor:not-allowed; box-shadow:none }
    .msg{ padding:12px 16px; font-weight:600; }
    .success{ color: var(--ok); }
    .error{ color: var(--err); }
    .inline-note{ font-size:.85rem; color:var(--muted); margin-left:10px }
  </style>
</head>
<body>
  <div class="card" role="region" aria-label="Competitor Crawl Request">
    <header>
      <div class="logo" aria-hidden="true"></div>
      <div>
        <h1>Competitor Crawl Request</h1>
        <div class="sub">Send a domain and optional scoping rules for the crawl</div>
      </div>
    </header>

    <form id="requestForm" novalidate>
      <div class="grid">
        <div class="full">
          <label for="competitors">Competitor URL / Domain</label>
          <input id="competitors" name="competitors" type="text" required placeholder="https://example.com or example.com" />
          <small class="help">A full URL or bare domain is fine ‚Äî we‚Äôll normalize it.</small>
        </div>

        <div>
          <label for="name">Your Name</label>
          <input id="name" name="name" type="text" required placeholder="Your name‚Ä¶" autocomplete="name" />
        </div>

        <div>
          <label for="email">Email (for report)</label>
          <input id="email" name="email" type="email" required placeholder="you@domain.com" autocomplete="email" inputmode="email" />
          <small class="help">We‚Äôll email the summary here.</small>
        </div>

        <div class="full">
          <label for="include_paths">Include paths (multi-select)</label>
          <select id="include_paths" multiple>
            <option value="/">/</option>
            <option value="/blog/">/blog/</option>
            <option value="/services/">/services/</option>
            <option value="/products/">/products/</option>
            <option value="/news/">/news/</option>
            <option value="/portfolio/">/portfolio/</option>
          </select>
          <div class="row">
            <input id="include_custom" type="text" placeholder="Custom paths (comma-separated, e.g. /guide/, /docs/ )" />
            <span class="pill" id="add_include">+ Add</span>
          </div>
          <small class="help">Hold Ctrl/‚åò to select multiple. Custom items are appended automatically.</small>
        </div>

        <div class="full">
          <label for="exclude_paths">Exclude paths (multi-select)</label>
          <select id="exclude_paths" multiple>
            <option value="/privacy">/privacy</option>
            <option value="/terms">/terms</option>
            <option value="/login">/login</option>
            <option value="/cart">/cart</option>
            <option value="/checkout">/checkout</option>
            <option value="/wp-admin">/wp-admin</option>
            <option value="/tag/">/tag/</option>
            <option value="/category/">/category/</option>
            <option value="/feed">/feed</option>
          </select>
          <div class="row">
            <input id="exclude_custom" type="text" placeholder="Custom paths (comma-separated, e.g. /search, /admin )" />
            <span class="pill" id="add_exclude">+ Add</span>
          </div>
          <small class="help">Use this to skip utility/low-value areas.</small>
        </div>

        <div>
          <label for="max_pages">Max pages</label>
          <select id="max_pages">
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20" selected>20</option>
            <option value="50">50</option>
            <option value="100">100</option>
            <option value="200">200</option>
          </select>
          <span class="inline-note">Default 20</span>
        </div>

        <div>
          <label for="crawl_depth">Crawl depth</label>
          <select id="crawl_depth">
            <option value="0">0 (only start URL)</option>
            <option value="1" selected>1</option>
            <option value="2">2</option>
            <option value="3">3</option>
          </select>
          <span class="inline-note">Default 1</span>
        </div>
      </div>

      <div class="submit">
        <div id="response" class="msg" aria-live="polite"></div>
        <button id="submitBtn" type="submit">Send Request</button>
      </div>
    </form>
  </div>

  <script>
    const $ = (id) => document.getElementById(id);

    function showMsg(text, type) {
      const el = $('response');
      el.className = 'msg ' + (type || '');
      el.textContent = text;
    }

    function normalizeUrl(input) {
      const s = (input || '').trim();
      if (!s) return '';
      return /^https?:\/\//i.test(s) ? s : `https://${s}`;
    }

    function selectedValues(selectEl) {
      return Array.from(selectEl.options)
        .filter(o => o.selected)
        .map(o => o.value.trim())
        .filter(Boolean);
    }

    function parseCustom(inputEl) {
      return (inputEl.value || '')
        .split(',')
        .map(s => s.trim())
        .filter(Boolean);
    }

    function addCustomToSelect(inputEl, selectEl) {
      parseCustom(inputEl).forEach(v => {
        const opt = document.createElement('option');
        opt.value = v;
        opt.textContent = v;
        opt.selected = true;
        selectEl.appendChild(opt);
      });
      inputEl.value = '';
    }

    // Wire add buttons
    $('add_include').addEventListener('click', () => addCustomToSelect($('include_custom'), $('include_paths')));
    $('add_exclude').addEventListener('click', () => addCustomToSelect($('exclude_custom'), $('exclude_paths')));

    $('requestForm').addEventListener('submit', async (e) => {
      e.preventDefault();
      showMsg('', '');

      if (!e.target.checkValidity()) {
        e.target.reportValidity();
        return;
      }

      const name  = $('name').value.trim();
      const email = $('email').value.trim();
      const competitors = normalizeUrl($('competitors').value.trim());

      const emailOk = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
      if (!emailOk) { $('email').focus(); return showMsg('‚ùå Please enter a valid email.', 'error'); }

      if (!competitors) { $('competitors').focus(); return showMsg('‚ùå Please enter a competitor URL.', 'error'); }

      // Merge dropdown selections + any custom text entries
      const includeSel = selectedValues($('include_paths'));
      const excludeSel = selectedValues($('exclude_paths'));
      const includeCustom = parseCustom($('include_custom'));
      const excludeCustom = parseCustom($('exclude_custom'));

      const include_paths = [...includeSel, ...includeCustom].join(', ');
      const exclude_paths = [...excludeSel, ...excludeCustom].join(', ');

      const max_pages   = $('max_pages').value;
      const crawl_depth = $('crawl_depth').value;

      const submitBtn = $('submitBtn');
      submitBtn.setAttribute('disabled','true');
      submitBtn.textContent = 'Sending‚Ä¶';

      try {
        const webhookUrl = 'https://n8nworkflow.eu/webhook-test/competitors'; // use production /webhook/... when you deploy
        const res = await fetch(webhookUrl, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name,
            email,
            competitors,
            include_paths,
            exclude_paths,
            max_pages,
            crawl_depth
          })
        });

        if (res.ok) {
          showMsg('‚úÖ Request sent. We‚Äôll email you when it‚Äôs processed.', 'success');
          e.target.reset();
        } else {
          const txt = await res.text().catch(() => '');
          showMsg('‚ùå Failed to send. ' + (txt || ''), 'error');
        }
      } catch (err) {
        showMsg('‚ùå Network error. Please try again.', 'error');
      } finally {
        submitBtn.removeAttribute('disabled');
        submitBtn.textContent = 'Send Request';
      }
    });
  </script>
</body>
</html>
