const priorityMap = {
  "Highest": "1",
  "High": "2", 
  "Medium": "3",
  "Low": "4",
  "Lowest": "5"
};

const sheetPriority = ($json['Priority'] || "").toString().trim();
const jiraPriority = priorityMap[sheetPriority] || "3";
const context = $json['Context'] || "No context provided";
const email = $json['Adresse e-mail'] || $json['Email'] || "No email provided";

// keep data from the  Google Sheets Trigger
const originalData = $input.first().json;

return {
  ...originalData,  // keep all data from trigger
  priority: jiraPriority,
  summary: context,
  description: `Email: ${email}\nContext: ${context}\nSteps to reproduce: ${$json['If bug, steps to reproduce'] || "N/A"}\nAcceptance criteria: ${$json['Acceptance criteria'] || "N/A"}`
};

