=<p>Hello, you just received a new Jira ticket created.</p>

<p><strong>Details:</strong></p>
<ul>
  <li><strong>Reference:</strong> {{$node["Cretate Jira Ticket"].json.key}}</li>
  <li><strong>URL:</strong> 
    <a href="https://YOURSITE.atlassian.net/browse/{{$node['Cretate Jira Ticket'].json.key}}">View ticket</a>
  </li>
  <li><strong>Title:</strong> {{
    (String($node["Normalize fields"].json.summary ?? '')
      .replace(/\s+/g,' ')
      .trim())
    || 'New issue from Google Form'
  }}</li>
  <li><strong>Priority:</strong> {{
    (()=>{
         const p = String($node["Normalize fields"].json.priority ?? $json.priority ?? '').toLowerCase().trim();
         const map = { '1':'Highest','2':'High','3':'Medium','4':'Low','5':'Lowest',
                       'highest':'Highest','high':'High','medium':'Medium','low':'Low','lowest':'Lowest' };
         return map[p] || (p || 'Medium');
       })()
  }}</li>
  <li><strong>Status:</strong> {{ String($json.status ?? 'Created') }}</li>
</ul>

<p><strong>Requester:</strong></p>
<ul>
  <li><strong>Email:</strong> {{
    $node["Trigger when row added"].json["Adresse e-mail"]
    || $node["Trigger when row added"].json.Email
    || $node["Normalize fields"].json["Adresse e-mail"]
    || $node["Normalize fields"].json.Email
