/* N8N Landing Page CSS ‚Äì Header-matching Blues */

/* Style the main form card */
.card {
  position: relative;
  max-width: 500px;
  margin: 20px auto;
  padding: 20px;
  background: white !important;
  border-radius: 12px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* Header bar with your header gradient */
.card:before {
  content: '';
  display: block;
  height: 50px;
  background: linear-gradient(135deg, #0A3E7B 0%, #1565C0 100%) !important;
  position: relative;
  margin: -20px -20px 30px -20px;
  border-radius: 12px 12px 0 0;
}

/* Titles in deep blue */
.form-header,
.card h1,
.card h2 {
  text-align: center !important;
  color: #0A3E7B !important;
  font-weight: 500 !important;
  font-size: 1.6em !important;
  margin: 20px 0 15px 0 !important;
  position: relative;
  z-index: 5;
}

/* Description copy in a cool neutral */
.card p {
  text-align: center !important;
  color: #475569 !important;
  position: relative;
  z-index: 5;
}

/* Inputs wrapper keeps above the gradient */
.inputs-wrapper {
  position: relative;
  z-index: 5;
}

/* Form fields with blue focus */
.card input[type="text"],
.card input[type="email"],
.card input[type="tel"],
.card input[type="number"],
.card textarea,
.card select {
  width: 100% !important;
  padding: 12px 16px !important;
  border: 2px solid #E5E7EB !important;
  border-radius: 8px !important;
  font-size: 16px !important;
  transition: border-color 0.3s ease !important;
  box-sizing: border-box !important;
}

.card input:focus,
.card textarea:focus,
.card select:focus {
  outline: none !important;
  border-color: #0A3E7B !important;
  box-shadow: 0 0 0 3px rgba(10, 62, 123, 0.1) !important;
}

/* Submit button in a mid-to-light blue gradient */
.card button[type="submit"],
.card input[type="submit"] {
  background: linear-gradient(135deg, #1565C0 0%, #64B5F6 100%) !important;
  color: white !important;
  border: none !important;
  padding: 15px 40px !important;
  border-radius: 10px !important;
  font-size: 18px !important;
  font-weight: 600 !important;
  cursor: pointer !important;
  width: 100% !important;
  margin-top: 10px !important;
  transition: all 0.3s ease !important;
  box-shadow: 0 4px 15px rgba(21, 101, 192, 0.3) !important;
  position: relative;
  z-index: 5;
}

.card button:hover,
.card input[type="submit"]:hover {
  transform: translateY(-2px) !important;
  box-shadow: 0 6px 20px rgba(21, 101, 192, 0.4) !important;
}

/* Page background to complement the pale side of your header */
body.vsc-initialized {
  background: #EEF6FF !important;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif !important;
  margin: 0 !important;
  padding: 20px !important;
}

/* Center container */
.container {
  max-width: 600px !important;
  margin: 0 auto !important;
}

.card .form-group {
  margin-top: 4px !important;
  margin-bottom: 4px !important;
}

/* Mobile tweaks */
@media (max-width: 768px) {
  .card {
    margin: 10px !important;
    padding: 15px !important;
  }
  .card:before {
    height: 50px !important;
    margin: -15px -15px 20px -15px !important;
  }
  .form-header,
  .card h1,
  .card h2 {
    font-size: 1.7em !important;
    margin: 20px 0 15px 0 !important;
  }
}

