parameters.jsCode:
// items passed in are the HTTP Request output
// which returns { value: [ /* events */ ] }
const events = $input.first().json.value || [];

// 2) map into slot objects
const newSlots = events.map(e => {
  // Parse ISO string into Date object
  const d = new Date(e.start.dateTime);

  // Format: Wednesday 20 August 2025 11:00
  const formatted = d.toLocaleString("en-GB", {
    weekday: "long",
    day: "2-digit",
    month: "long",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
    hour12: false
  });

  return {
    time: formatted,
    subject: e.subject,
    start: e.start.dateTime,
    end: e.end.dateTime,
    id: e.id
  };
});

// 3) return each slot as its own item
return newSlots.map(slot => ({ json: slot }));

// 1. Pull in all event objects
const events = items.map(item => item.json);

// 2. Build your dropdown object
const dropdown = {
  fieldLabel: "Select one of our available time",
  fieldType: "dropdown",
  fieldOptions: {
    values: events.map(e => {
      // Parse the time string into a Date object
      return { option: e.time };
    })
  },
  requiredField: true
};

// 3. Combine into a single JS array: dropdown first, then each event
const combined = [{ form: dropdown }, ...events];

return combined.map(json => ({ json }));

