// Calculate experience and skill metrics
const resume = $input.first().json.data;
const currentDate = new Date();

// Calculate total years of experience
let totalExperience = 0;
let skillExperience = {};

if (resume.workExperience) {
  resume.workExperience.forEach(job => {
    const startDate = new Date(job.startDate);
    const endDate = job.endDate === 'Present' ? currentDate : new Date(job.endDate);
    const years = (endDate - startDate) / (1000 * 60 * 60 * 24 * 365);
    totalExperience += years;

    // Track experience per technology
    if (job.technologies) {
      job.technologies.forEach(tech => {
        skillExperience[tech] = (skillExperience[tech] || 0) + years;
      });
    }
  });
}

// Create skill proficiency scores
const skillScores = {};
if (resume.skills?.technical) {
  resume.skills.technical.forEach(skill => {
    const experience = skillExperience[skill] || 0;
    let score = 0;
    if (experience >= 5) score = 5;
    else if (experience >= 3) score = 4;
    else if (experience >= 1) score = 3;
    else if (experience > 0) score = 2;
    else score = 1;

    skillScores[skill] = {
      yearsExperience: Math.round(experience * 10) / 10,
      proficiencyScore: score
    };
  });
}

return [{
  json: {
    candidateProfile: resume,
    metrics: {
      totalYearsExperience: Math.round(totalExperience * 10) / 10,
      skillScores: skillScores,
      educationLevel: resume.education?.[0]?.degree || 'Not specified',
      certificationCount: resume.certifications?.length || 0,
      languageCount: resume.skills?.languages?.length || 0
    },
    processedAt: new Date().toISOString()
  }
}];
