// Process receipt data and tax categorization
const receiptData = $node['PDF Vector - Extract Receipt'].json.data;
const taxCategory = $node['PDF Vector - Tax Categorization'].json.answer;

// Validate financial calculations
let validationErrors = [];
if (receiptData.items && receiptData.items.length > 0) {
  const calculatedSubtotal = receiptData.items.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
  if (Math.abs(calculatedSubtotal - receiptData.financial.subtotal) > 0.02) {
    validationErrors.push('Item totals do not match subtotal');
  }
}

// Calculate tax consistency
const expectedTax = receiptData.financial.subtotal * (receiptData.financial.taxRate / 100);
if (Math.abs(expectedTax - receiptData.financial.taxAmount) > 0.02) {
  validationErrors.push('Tax calculation inconsistency');
}

// Determine expense category and deductibility
let expenseCategory = 'Other';
let deductiblePercentage = 100;
let taxNotes = '';

if (taxCategory.toLowerCase().includes('meal')) {
  expenseCategory = 'Meals & Entertainment';
  deductiblePercentage = 50; // Typical meal deduction
  taxNotes = 'Business meal - 50% deductible';
} else if (taxCategory.toLowerCase().includes('travel')) {
  expenseCategory = 'Travel';
  deductiblePercentage = 100;
  taxNotes = 'Business travel expense';
} else if (taxCategory.toLowerCase().includes('supplies')) {
  expenseCategory = 'Office Supplies';
  deductiblePercentage = 100;
  taxNotes = 'Business supplies';
}

// Create processed expense record
const processedExpense = {
  // Receipt data
  merchant: receiptData.merchant.name,
  date: receiptData.transaction.date,
  amount: receiptData.financial.total,
  currency: receiptData.financial.currency || 'USD',

  // Tax information
  expenseCategory,
  deductiblePercentage,
  deductibleAmount: (receiptData.financial.total * deductiblePercentage / 100).toFixed(2),
  taxNotes,

  // Original data
  originalReceipt: receiptData,
  aiCategorization: taxCategory,

  // Validation
  isValid: validationErrors.length === 0,
  validationErrors,

  // Metadata
  processedAt: new Date().toISOString(),
  taxYear: new Date(receiptData.transaction.date).getFullYear()
};

return [{ json: processedExpense }];
