// Rank papers by relevance and citations
const papers = $input.all().map(item => item.json);
const searchTopic = $node['Set Search Parameters'].json.topic;

// Calculate relevance scores
const scoredPapers = papers.map(paper => {
  let score = 0;

  // Citation score (normalized)
  const maxCitations = Math.max(...papers.map(p => p.totalCitations || 0));
  const citationScore = (paper.totalCitations || 0) / (maxCitations || 1) * 40;
  score += citationScore;

  // Recency score
  const paperYear = parseInt(paper.year);
  const currentYear = new Date().getFullYear();
  const recencyScore = Math.max(0, 20 - (currentYear - paperYear) * 2);
  score += recencyScore;

  // Title relevance
  const topicWords = searchTopic.toLowerCase().split(' ');
  const titleWords = paper.title.toLowerCase();
  const titleMatches = topicWords.filter(word => titleWords.includes(word)).length;
  score += titleMatches * 10;

  // Abstract relevance
  if (paper.abstract) {
    const abstractWords = paper.abstract.toLowerCase();
    const abstractMatches = topicWords.filter(word => abstractWords.includes(word)).length;
    score += abstractMatches * 5;
  }

  return {
    ...paper,
    relevanceScore: Math.round(score),
    rankingDetails: {
      citationScore: Math.round(citationScore),
      recencyScore,
      titleRelevance: titleMatches,
      abstractRelevance: abstractMatches || 0
    }
  };
});

// Sort by score and limit to top N
const maxPapers = $node['Set Search Parameters'].json.maxPapers;
const topPapers = scoredPapers
  .sort((a, b) => b.relevanceScore - a.relevanceScore)
  .slice(0, maxPapers);

return topPapers.map(paper => ({ json: paper }));

