// Wait for all papers to be processed
const allEntries = $input.all().map(item => item.json);

// Group papers by themes/methodologies
const themes = {
  'Machine Learning Models': [],
  'Clinical Applications': [],
  'Data Processing': [],
  'Evaluation Studies': [],
  'Review Papers': [],
  'Other': []
};

// Categorize papers (simplified - in production use NLP)
allEntries.forEach(entry => {
  const review = entry.reviewEntry.toLowerCase();
  if (review.includes('neural network') || review.includes('deep learning')) {
    themes['Machine Learning Models'].push(entry);
  } else if (review.includes('clinical') || review.includes('patient')) {
    themes['Clinical Applications'].push(entry);
  } else if (review.includes('preprocessing') || review.includes('data processing')) {
    themes['Data Processing'].push(entry);
  } else if (review.includes('evaluation') || review.includes('comparison')) {
    themes['Evaluation Studies'].push(entry);
  } else if (review.includes('review') || review.includes('survey')) {
    themes['Review Papers'].push(entry);
  } else {
    themes['Other'].push(entry);
  }
});

// Generate literature review document
let reviewDocument = `# Literature Review: ${$node['Set Search Parameters'].json.topic}\n\n`;
reviewDocument += `Generated on: ${new Date().toLocaleDateString()}\n\n`;
reviewDocument += `## Summary\n\n`;
reviewDocument += `This review analyzes ${allEntries.length} papers published between ${$node['Set Search Parameters'].json.yearFrom} and ${$node['Set Search Parameters'].json.yearTo} on the topic of ${$node['Set Search Parameters'].json.topic}.\n\n`;

// Add themed sections
Object.entries(themes).forEach(([theme, papers]) => {
  if (papers.length > 0) {
    reviewDocument += `## ${theme} (${papers.length} papers)\n\n`;
    papers.forEach(paper => {
      reviewDocument += `### ${paper.paperTitle}\n\n`;
      reviewDocument += paper.reviewEntry + '\n\n';
    });
  }
});

// Add bibliography
reviewDocument += `## Bibliography\n\n`;
allEntries.forEach((entry, index) => {
  const citation = entry.reviewEntry.split('Suggested citation:')[1] || 'Citation not available';
  reviewDocument += `${index + 1}. ${citation.trim()}\n\n`;
});

return [{
  json: {
    reviewDocument,
    totalPapers: allEntries.length,
    themes: Object.entries(themes).map(([theme, papers]) => ({
      theme,
      count: papers.length
    })),
    generatedAt: new Date().toISOString()
  }
}];
