  return payload;

} else {
  // âŒ Status not ok â†’ return error message
  return {
    status: "error",
    message: "Missing Mandatory field(s) or status not ok"
  };
}

// Function Item node
// This makes sure you always get the invoice JSON payload, even if Get data is empty.

// 1. Get what Google Sheets returned
const sheetItem = $items("Get data") && $items("Get data")[0] 
  ? $items("Get data")[0].json 
  : {};

// 2. Check if it's empty
const sheetEmpty = Object.keys(sheetItem).length === 0;

// 3. If empty â†’ use your original invoice JSON instead
//    >>> REPLACE the placeholder below with the node that has your invoice JSON <<<
// Example: $items("Edit Fields")[0].json OR paste your raw invoice object
const invoicePayload = sheetEmpty 
  ? (
      // ðŸ”½ PLACEHOLDER: replace this line with your real JSON source
      $('Make data in json structure format').first().json.output
    )
  : sheetItem;

// 4. Return only the payload JSON
return invoicePayload;

// Function Item node
// Check if mandatory fields are present before appending to Google Sheet

// 1. Get the invoice payload (replace with your correct source node if needed)
const payload = $('Make data in json structure format').first().json.output;

// 2. Define mandatory fields
const mandatoryFields = ["invoice_id", "shop_name", "date", "Total", "items"];

// 3. Check missing fields
const missing = mandatoryFields.filter(f => {
  if (f === "items") {
    return !payload.items || !Array.isArray(payload.items) || payload.items.length === 0;
  }
  return !payload[f] || payload[f].toString().trim() === "";
});

// 4. If missing â†’ return error
if (missing.length > 0) {
  return {
    json: {
      status: "error",
      message: "Missing Mandatory field(s)",
      missing_fields: missing
    }
  };
}

// 5. If all present â†’ return full payload
return {
  json: {
    status: "ok",
    payload // ðŸ”½ This is the invoice JSON youâ€™ll pass to Google Sheets
  }
};

