// Extract and merge JSON product data from markdown-formatted strings

const results = [];

for (const item of items) {
  const raw = item.json.content;

  // Remove the ```json and ``` wrappers
  const cleaned = raw.replace(/^```json\n/, "").replace(/\n```$/, "");

  try {
    const parsed = JSON.parse(cleaned);
    results.push(...parsed);
  } catch (e) {
    // Optional: you can skip or throw error depending on your use case
    throw new Error("Failed to parse product JSON: " + e.message);
  }
}

// Return as individual items
return results.map(product => ({ json: product }));

