// n8n JavaScript Code for Stock Data Transformation - With Price Trends
// Comprehensive output including trend analysis for LLMs

function calculateMovingAverage(prices, period) {
  if (prices.length < period) return null;
  const sum = prices.slice(0, period).reduce((a, b) => a + b, 0);
  return Math.round((sum / period) * 100) / 100;
}

function calculateTrendDirection(prices) {
  if (prices.length < 3) return 'INSUFFICIENT_DATA';

  const recent = prices.slice(0, 3);
  const older = prices.slice(3, 6);

  const recentAvg = recent.reduce((a, b) => a + b, 0) / recent.length;
  const olderAvg = older.length > 0 ? older.reduce((a, b) => a + b, 0) / older.length : recentAvg;

  const change = ((recentAvg - olderAvg) / olderAvg) * 100;

  if (change > 2) return 'STRONG_UPTREND';
  if (change > 0.5) return 'UPTREND';
  if (change < -2) return 'STRONG_DOWNTREND';
  if (change < -0.5) return 'DOWNTREND';
  return 'SIDEWAYS';
}

function identifySupportResistance(prices, highs, lows) {
  const sortedPrices = [...prices].sort((a, b) => a - b);
  const maxPrice = Math.max(...highs);
  const minPrice = Math.min(...lows);

  return {
    resistance: maxPrice,
    support: minPrice,
    median: sortedPrices[Math.floor(sortedPrices.length / 2)]
  };
}

function transformStockDataForLLM(stockData) {
  const result = {
    symbol: '',
    overview: {},
    price_trends: {},
    technical_analysis: {},
    multi_timeframe: {},
    llm_summary: ''
  };

  let dailyData = null;
  let weeklyData = null;
  let intraDayData = null;

  // Separate data by timeframes
  stockData.forEach(dataSet => {
    const meta = dataSet.meta;
    const interval = meta.interval;

    result.symbol = meta.symbol;

    if (interval === '1day') dailyData = dataSet;
    if (interval === '1week') weeklyData = dataSet;
    if (interval === '4h') intraDayData = dataSet;
  });

  if (!dailyData || !dailyData.values.length) {
    throw new Error('No daily data found');
  }

  const dailyValues = dailyData.values;
  const latest = dailyValues[0];

  // Basic overview
  result.overview = {
    symbol: result.symbol,
    current_price: parseFloat(latest.close),
    date: latest.datetime,
    daily_change: dailyValues[1] ? parseFloat(latest.close) - parseFloat(dailyValues[1].close) : 0,
    daily_change_percent: dailyValues[1] ? 
      Math.round(((parseFloat(latest.close) - parseFloat(dailyValues[1].close)) / parseFloat(dailyValues[1].close)) * 100 * 100) / 100 : 0,
    volume: parseInt(latest.volume),
    day_range: `$${latest.low} - $${latest.high}`
  };

  // Extract price arrays for analysis
  const closePrices = dailyValues.map(d => parseFloat(d.close));
  const highPrices = dailyValues.map(d => parseFloat(d.high));
  const lowPrices = dailyValues.map(d => parseFloat(d.low));
  const volumes = dailyValues.map(d => parseInt(d.volume));

  // Price trends analysis
  result.price_trends = {
    trend_direction: calculateTrendDirection(closePrices),
    moving_averages: {
      ma_5: calculateMovingAverage(closePrices, 5),
      ma_10: calculateMovingAverage(closePrices, 10),
      ma_20: calculateMovingAverage(closePrices, 20)
    },
    price_position: {
      above_ma5: closePrices[0] > calculateMovingAverage(closePrices, 5),
      above_ma10: closePrices[0] > calculateMovingAverage(closePrices, 10),
      above_ma20: closePrices[0] > calculateMovingAverage(closePrices, 20)
    },
    recent_performance: {
      last_5_days: dailyValues.slice(0, 5).map(d => ({
        date: d.datetime,
        close: parseFloat(d.close),
        change_from_prev: dailyValues.indexOf(d) < dailyValues.length - 1 ? 
          parseFloat(d.close) - parseFloat(dailyValues[dailyValues.indexOf(d) + 1].close) : 0,
        volume: parseInt(d.volume)
      })),
      weekly_change: closePrices.length >= 7 ? 
        Math.round(((closePrices[0] - closePrices[6]) / closePrices[6]) * 100 * 100) / 100 : 0,
      monthly_change: closePrices.length >= 30 ? 
        Math.round(((closePrices[0] - closePrices[29]) / closePrices[29]) * 100 * 100) / 100 : 0
    }
  };

  // Technical analysis
  const supportResistance = identifySupportResistance(closePrices.slice(0, 30), highPrices.slice(0, 30), lowPrices.slice(0, 30));
  const avgVolume = volumes.slice(0, 20).reduce((a, b) => a + b, 0) / Math.min(20, volumes.length);

  result.technical_analysis = {
    support_resistance: supportResistance,
    volume_analysis: {
      current_volume: volumes[0],
      average_volume_20d: Math.round(avgVolume),
      volume_ratio: Math.round((volumes[0] / avgVolume) * 100) / 100,
      volume_trend: volumes[0] > avgVolume * 1.5 ? 'HIGH' : volumes[0] < avgVolume * 0.5 ? 'LOW' : 'NORMAL'
    },
    volatility: {
      daily_volatility: Math.round(((parseFloat(latest.high) - parseFloat(latest.low)) / parseFloat(latest.close)) * 100 * 100) / 100,
      recent_volatility: Math.round((Math.max(...highPrices.slice(0, 10)) - Math.min(...lowPrices.slice(0, 10))) / closePrices[0] * 100 * 100) / 100
    },
    momentum_indicators: {
      price_vs_support: Math.round(((closePrices[0] - supportResistance.support) / supportResistance.support) * 100 * 100) / 100,
      price_vs_resistance: Math.round(((supportResistance.resistance - closePrices[0]) / closePrices[0]) * 100 * 100) / 100
    }
  };

  // Multi-timeframe analysis
  result.multi_timeframe = {
    daily: {
      trend: result.price_trends.trend_direction,
      last_close: closePrices[0]
    }
  };

  // Add weekly analysis if available
  if (weeklyData && weeklyData.values.length > 1) {
    const weeklyPrices = weeklyData.values.map(d => parseFloat(d.close));
    result.multi_timeframe.weekly = {
      trend: calculateTrendDirection(weeklyPrices),
      weekly_change: Math.round(((weeklyPrices[0] - weeklyPrices[1]) / weeklyPrices[1]) * 100 * 100) / 100
    };
  }

  // Add intraday analysis if available
  if (intraDayData && intraDayData.values.length > 1) {
    const intradayPrices = intraDayData.values.map(d => parseFloat(d.close));
    result.multi_timeframe.intraday = {
      trend: calculateTrendDirection(intradayPrices),
      recent_sessions: intraDayData.values.slice(0, 6).map(d => ({
        datetime: d.datetime,
        close: parseFloat(d.close)
      }))
    };
  }

  // Generate comprehensive LLM summary
  const ma5 = result.price_trends.moving_averages.ma_5;
  const ma20 = result.price_trends.moving_averages.ma_20;
  const trendStrength = result.price_trends.trend_direction;
  const volumeStatus = result.technical_analysis.volume_analysis.volume_trend;

  result.llm_summary = `${result.symbol} Technical Analysis Summary:

CURRENT STATUS: Trading at $${result.overview.current_price} (${result.overview.daily_change >= 0 ? '+' : ''}${result.overview.daily_change_percent}% today)

TREND ANALYSIS:
- Overall Trend: ${trendStrength}
- Price vs 5-day MA ($${ma5}): ${closePrices[0] > ma5 ? 'ABOVE' : 'BELOW'}
- Price vs 20-day MA ($${ma20}): ${closePrices[0] > ma20 ? 'ABOVE' : 'BELOW'}
- Weekly Performance: ${result.price_trends.recent_performance.weekly_change >= 0 ? '+' : ''}${result.price_trends.recent_performance.weekly_change}%

TECHNICAL LEVELS:
- Support: $${supportResistance.support}
- Resistance: $${supportResistance.resistance}
- Distance to Support: ${result.technical_analysis.momentum_indicators.price_vs_support}%

VOLUME & MOMENTUM:
- Volume: ${volumeStatus} (${result.technical_analysis.volume_analysis.volume_ratio}x average)
- Daily Volatility: ${result.technical_analysis.volatility.daily_volatility}%

KEY INSIGHT: Stock is ${trendStrength.toLowerCase()}, trading ${closePrices[0] > ma20 ? 'above' : 'below'} key moving averages with ${volumeStatus.toLowerCase()} volume activity.`;

  return result;
}

// Main execution for n8n
try {
  if (!$json || !$json.data || !Array.isArray($json.data)) {
    throw new Error('Expected input format: { "data": [...] }');
  }

  const transformedData = transformStockDataForLLM($json.data);

  return {
    json: transformedData
  };

} catch (error) {
  return {
    json: {
      error: "Data transformation failed",
      details: error.message,
      expected_format: "{ data: [{ meta: {...}, values: [...] }] }"
    }
  };
}
