={{ (function(){
  const base = 'https://api.upbit.com/v1/candles/';
  const tf = $fromAI('tf', '15m', 'string');
  const m = (tf || '').trim();

  // seconds (e.g., 1s, 30s)
  if (/^\d+s$/i.test(m)) {
    return base + 'seconds';
  }

  // minutes direct (e.g., 1m, 3m, 5m, 10m, 15m, 30m, 60m, 240m)
  const minMatch = m.match(/^(\d+)m$/i);
  if (minMatch) {
    const unit = minMatch[1];
    return base + 'minutes/' + unit;
  }

  // hours mapped to minutes
  const hourMap = { '1':'60', '2':'120', '4':'240' };
  const hrMatch = m.match(/^(\d+)h$/i);
  if (hrMatch && hourMap[hrMatch[1]]) {
    return base + 'minutes/' + hourMap[hrMatch[1]];
  }

  // days, weeks, months, years
  if (/^\d+d$/i.test(m)) return base + 'days';
  if (/^\d+w$/i.test(m)) return base + 'weeks';
  if (/^\d+M$/i.test(m)) return base + 'months';
  if (/^\d+y$/i.test(m)) return base + 'years';

  // fallback: 15m
  return base + 'minutes/15';
})() }}
