parameters.jsCode:
// Collect all inputs
const allInputs = $input.all();

// Flatten all job contents into a single string
const raw = allInputs
  .map(item => item.json.message?.content || "")
  .join("\n\n");

// If nothing found, stop
if (!raw.trim()) {
  return [];
}

// Split the raw content by job postings
const postings = raw
  .split('--- END JOB POSTING ---')
  .filter(p => p.trim() !== '');

const structuredPostings = postings.map((post, index) => {
  const jobTitle = post.match(/Job Title:\s*(.+)/)?.[1]?.trim() || "Not specified";
  const url = post.match(/URL:\s*(.+)/)?.[1]?.trim() || "Not specified";
  const postingDate = post.match(/Posted|Posting Date:\s*(.+)/)?.[1]?.trim() || "Not specified";

  const descriptionMatch = post.match(/Job Description:\s*([\s\S]*?)(?:\nRequirements:|\n6\.)/) ||
                           post.match(/Job Description:\s*([\s\S]*)/);
  const jobDescription = descriptionMatch?.[1]?.trim() || "Not specified";

  const requirementsMatch = post.match(/Requirements:\s*([\s\S]*?)(?:\n6\.|$)/);
  const requirements = requirementsMatch
    ? requirementsMatch[1]
        .split('\n')
        .map(r => r.replace(/^-?\s*/, '').trim())
        .filter(Boolean)
    : [];

  const companyName = post.match(/Company Name:\s*(.+)/)?.[1]?.trim() || "Not specified";
  const companyDescription = post.match(/Company Description:\s*(.+)/)?.[1]?.trim() || "Not specified";
  const companyWebsite = post.match(/Company Website:\s*(.+)/)?.[1]?.trim() || "Not specified";
  const location = post.match(/Location:\s*(.+)/)?.[1]?.trim() || "Not specified";

  return {
    json: {
      posting_number: index + 1,
      job_title: jobTitle,
      url,
      posting_date: postingDate,
      job_description: jobDescription,
      requirements,
      company_name: companyName,
      company_description: companyDescription,
      company_website: companyWebsite,
      location
    }
  };
});

return structuredPostings;
