const allTasks = items.map(item => item.json);
const summary = allTasks.map(task => `- ${task.title}`).join('\n');

return [
  {
    json: {
      summary
    }
  }
];

// Fixed part of the URL
const baseUrl = "https://es.easyproject.com/issues/"; // here you need to add your Easy Redmine URL."

// Variable part from previous node
const variablePart =  $('Split Out Tasks').item.json.issues.id ;// e.g., "12345"

// Original text to enrich
const text = $('Split Out Tasks').item.json.issues.description; // e.g., "This is the task description."

// Construct the full link
const fullLink = `${baseUrl}${variablePart}`;

// Enrich the text
const enrichedText = `${fullLink}\n\n${text}`;

return {
  json: {
    enrichedText
  }
};
