parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Trello Sub-Agent Output",
  "type": "object",
  "properties": {
    "status": {
      "type": "string",
      "enum": ["exists", "created", "error"],
      "description": "Result of the Trello sync check."
    },
    "card_id": {
      "type": "string",
      "description": "ID of the Trello card (if exists or created)."
    },
    "list_id": {
      "type": "string",
      "description": "ID of the list where the card already exists (only when status = 'exists')."
    },
    "created_in_default_list": {
      "type": "boolean",
      "description": "True if a new card was created in the default list (only when status = 'created')."
    },
    "message": {
      "type": "string",
      "description": "Error message if status = 'error'."
    }
  },
  "required": ["status"],
  "additionalProperties": false
}
