// Get form data - handle both form and webhook inputs
const rawData = $input.item.json;
const formData = rawData.body || rawData; // Use body if it exists (webhook), otherwise use raw data (form)

// Helper function to extract handles from text
function parseKnownHandles(text) {
  if (!text) return {};

  const handles = {};
  const lines = text.split('\n');

  lines.forEach(line => {
    // Check for LinkedIn URLs or mentions
    if (line.toLowerCase().includes('linkedin')) {
      const urlMatch = line.match(/linkedin\.com\/in\/([^/\s?]+)/);
      const textMatch = line.match(/linkedin:\s*([A-Za-z0-9-]+)/i);
      if (urlMatch) handles.linkedin = urlMatch[1];
      else if (textMatch) handles.linkedin = textMatch[1];
    }

    // Check for Twitter/X handles
    if (line.toLowerCase().includes('twitter') || line.toLowerCase().includes('x:') || line.includes('@')) {
      const match = line.match(/@([A-Za-z0-9_]+)|twitter:\s*([A-Za-z0-9_]+)|x:\s*([A-Za-z0-9_]+)/i);
      if (match) handles.twitter = match[1] || match[2] || match[3];
    }

    // Check for GitHub
    if (line.toLowerCase().includes('github')) {
      const urlMatch = line.match(/github\.com\/([A-Za-z0-9-]+)/);
      const textMatch = line.match(/github:\s*([A-Za-z0-9_-]+)/i);
      if (urlMatch) handles.github = urlMatch[1];
      else if (textMatch) handles.github = textMatch[1];
    }

    // Check for Instagram
    if (line.toLowerCase().includes('instagram')) {
      const urlMatch = line.match(/instagram\.com\/@?([A-Za-z0-9_.]+)/);
      const textMatch = line.match(/instagram:\s*@?([A-Za-z0-9_.]+)/i);
      if (urlMatch) handles.instagram = urlMatch[1];
      else if (textMatch) handles.instagram = textMatch[1];
    }

    // Check for YouTube
    if (line.toLowerCase().includes('youtube')) {
      const channelMatch = line.match(/youtube\.com\/(?:c\/|@)([A-Za-z0-9_-]+)/);
      const textMatch = line.match(/youtube:\s*@?([A-Za-z0-9_-]+)/i);
      if (channelMatch) handles.youtube = channelMatch[1];
      else if (textMatch) handles.youtube = textMatch[1];
    }

    // Check for Facebook
    if (line.toLowerCase().includes('facebook')) {
      const urlMatch = line.match(/facebook\.com\/([A-Za-z0-9.]+)/);
      const textMatch = line.match(/facebook:\s*([A-Za-z0-9.]+)/i);
      if (urlMatch) handles.facebook = urlMatch[1];
      else if (textMatch) handles.facebook = textMatch[1];
    }

    // Check for TikTok
    if (line.toLowerCase().includes('tiktok')) {
      const urlMatch = line.match(/tiktok\.com\/@([A-Za-z0-9_.]+)/);
      const textMatch = line.match(/tiktok:\s*@?([A-Za-z0-9_.]+)/i);
      if (urlMatch) handles.tiktok = urlMatch[1];
      else if (textMatch) handles.tiktok = textMatch[1];
    }
  });

  return handles;
}

// Generate name variations for searching
function generateNameVariations(fullName) {
  if (!fullName) return [];

  const cleanName = fullName.trim();
  const parts = cleanName.split(' ').filter(p => p); // Remove empty parts
  const variations = new Set([cleanName]);

  if (parts.length === 1) {
    // Single name - just return it
    variations.add(parts[0]);
  } else if (parts.length === 2) {
    const [first, last] = parts;
    variations.add(`${first} ${last}`);
    variations.add(`${first}.${last}`);
    variations.add(`${first}${last}`);
    variations.add(`${first[0]}. ${last}`);
    variations.add(`${first} ${last[0]}.`);
    variations.add(`${last} ${first}`);
    variations.add(`${last}, ${first}`);
  } else if (parts.length === 3) {
    const [first, middle, last] = parts;
    variations.add(`${first} ${middle} ${last}`);
    variations.add(`${first} ${last}`);
    variations.add(`${first} ${middle[0]}. ${last}`);
    variations.add(`${first[0]}. ${middle[0]}. ${last}`);
    variations.add(`${last}, ${first} ${middle}`);
    variations.add(`${last}, ${first}`);
  } else if (parts.length >= 4) {
    // Handle longer names (like Jean-Claude Van Damme Jr.)
    // Take first and last parts as primary
    const first = parts[0];
    const last = parts[parts.length - 1];
    const middle = parts.slice(1, -1).join(' ');

    variations.add(cleanName); // Full name
    variations.add(`${first} ${last}`);
    variations.add(`${last}, ${first}`);
    if (middle) {
      variations.add(`${first} ${middle[0]}. ${last}`);
      variations.add(`${last}, ${first} ${middle}`);
    }
  }

  // Return as array
  return Array.from(variations);
}

// Build search queries for each platform
function buildSearchQueries(name, company, location, platform) {
  const queries = [];
  const baseQuery = `"${name}"`;

  switch(platform.toLowerCase()) {
    case 'linkedin':
      queries.push(`${baseQuery} site:linkedin.com/in`);
      if (company) queries.push(`${baseQuery} "${company}" site:linkedin.com`);
      if (location) queries.push(`${baseQuery} "${location}" site:linkedin.com`);
      break;

    case 'x/twitter':
      queries.push(`${baseQuery} site:twitter.com`);
      queries.push(`${baseQuery} site:x.com`);
      if (company) queries.push(`${baseQuery} "${company}" twitter`);
      break;

    case 'github':
      queries.push(`${baseQuery} site:github.com`);
      if (company) queries.push(`${baseQuery} "${company}" github`);
      break;

    case 'instagram':
      queries.push(`${baseQuery} site:instagram.com`);
      if (location) queries.push(`${baseQuery} "${location}" instagram`);
      break;

    case 'youtube':
      queries.push(`${baseQuery} site:youtube.com/@`);
      queries.push(`${baseQuery} site:youtube.com/c/`);
      if (company) queries.push(`${baseQuery} YouTube channel`);
      break;

    case 'tiktok':
      queries.push(`${baseQuery} site:tiktok.com/@`);
      queries.push(`${baseQuery} TikTok`);
      break;

    case 'facebook':
      queries.push(`${baseQuery} site:facebook.com`);
      if (location) queries.push(`${baseQuery} "${location}" facebook`);
      break;

    case 'reddit':
      queries.push(`${baseQuery} site:reddit.com/user`);
      queries.push(`${baseQuery} reddit user`);
      break;

    case 'pinterest':
      queries.push(`${baseQuery} site:pinterest.com`);
      break;

    case 'snapchat':
      queries.push(`${baseQuery} snapchat`);
      queries.push(`${baseQuery} "snapcode"`);
      break;
  }

  return queries;
}

// Validate required fields
const errors = [];
if (!formData['Full Name']?.trim()) {
  errors.push('Full Name is required');
}
if (!formData['Search Depth']) {
  errors.push('Search Depth is required');
}
if (!formData['Platforms to Search'] || formData['Platforms to Search'].length === 0) {
  errors.push('At least one platform must be selected');
}

// Process the form data
const validatedData = {
  // Original inputs
  fullName: formData['Full Name']?.trim() || '',
  email: formData['Email Address']?.trim() || '',
  company: formData['Company/Organization']?.trim() || '',
  location: formData['Location']?.trim() || '',
  searchDepth: formData['Search Depth'] || 'Comprehensive - Full analysis',
  platformsToSearch: formData['Platforms to Search'] || [],

  // Parsed and generated data
  knownHandles: parseKnownHandles(formData['Known Social Handles']),
  nameVariations: generateNameVariations(formData['Full Name']),

  // Search configuration
  searchConfig: {
    depth: formData['Search Depth']?.includes('Basic') ? 'basic' : 
           formData['Search Depth']?.includes('Deep') ? 'deep' : 'comprehensive',
    maxResultsPerPlatform: formData['Search Depth']?.includes('Basic') ? 5 : 
                           formData['Search Depth']?.includes('Deep') ? 20 : 10
  },

  // Validation
  isValid: errors.length === 0,
  validationErrors: errors,

  // Timestamp
  processedAt: new Date().toISOString()
};

// Build search queries for each selected platform
validatedData.searchQueries = {};
const platforms = formData['Platforms to Search'] || [];

if (validatedData.isValid) {
  platforms.forEach(platform => {
    validatedData.searchQueries[platform] = [];

    // Add queries for each name variation
    validatedData.nameVariations.forEach(nameVar => {
      const queries = buildSearchQueries(nameVar, formData['Company/Organization'], formData['Location'], platform);
      validatedData.searchQueries[platform].push(...queries);
    });

    // Remove duplicates
    validatedData.searchQueries[platform] = [...new Set(validatedData.searchQueries[platform])];
  });
}

// Add summary
validatedData.summary = {
  totalPlatforms: platforms.length,
  hasKnownHandles: Object.keys(validatedData.knownHandles).length > 0,
  nameVariationCount: validatedData.nameVariations.length,
  totalSearchQueries: Object.values(validatedData.searchQueries).flat().length
};

return validatedData;

// Get the AI agent's output
const agentOutput = $input.item.json;

// Helper function to normalize platform names
function normalizePlatformName(platform) {
  const mapping = {
    'LinkedIn': 'linkedin',
    'X/Twitter': 'twitter',
    'Twitter': 'twitter',
    'X': 'twitter',
    'Instagram': 'instagram',
    'GitHub': 'github',
    'Youtube': 'youtube',
    'YouTube': 'youtube',
    'TikTok': 'tiktok',
    'Facebook': 'facebook',
    'Reddit': 'reddit',
    'Pinterest': 'pinterest',
    'Snapchat': 'snapchat'
  };

  return mapping[platform] || platform.toLowerCase();
}

// Parse the agent's output
let discoveredProfiles = {};
let metadata = {
  searchQueriesUsed: 0,
  platformsChecked: 0,
  profilesFound: 0,
  highConfidenceProfiles: 0,
  timeTakenSeconds: 0,
  searchStartTime: new Date().toISOString(),
  searchEndTime: new Date().toISOString()
};

// Parse the agent output
let parsedData = null;
try {
  if (agentOutput.output && typeof agentOutput.output === 'string') {
    // Parse the JSON string in the output field
    parsedData = JSON.parse(agentOutput.output);
  } else if (agentOutput.output && typeof agentOutput.output === 'object') {
    parsedData = agentOutput.output;
  } else if (agentOutput.profiles) {
    parsedData = { profiles: agentOutput.profiles };
  }
} catch (e) {
  console.error('Error parsing agent output:', e);
}

// Process the profiles OBJECT format (not array!)
if (parsedData && parsedData.profiles && typeof parsedData.profiles === 'object') {
  // Iterate over the platforms (object keys)
  Object.entries(parsedData.profiles).forEach(([platform, profilesArray]) => {
    const normalizedPlatform = normalizePlatformName(platform);

    // Check if this platform has any profiles found
    if (profilesArray && Array.isArray(profilesArray) && profilesArray.length > 0) {
      // Take the first (most confident) profile for each platform
      const profile = profilesArray[0];

      discoveredProfiles[normalizedPlatform] = {
        handle: profile.handle || profile.username || '',
        url: profile.url || '',
        confidence: profile.confidence_score || profile.confidence || 0.5,
        markers: profile.matching_factors || [],
        profileData: {
          name: profile.name || '',
          bio: profile.bio_description || profile.bio || '',
          followers: profile.follower_count || profile.followers_connections || profile.followers || '',
          company: profile.company || '',
          location: profile.location || '',
          notes: profile.notes || ''
        }
      };

      metadata.profilesFound++;
      metadata.platformsChecked++;

      if (profile.confidence_score > 0.8 || profile.confidence > 0.8) {
        metadata.highConfidenceProfiles++;
      }
    } else {
      // Platform was checked but no profiles found
      metadata.platformsChecked++;
    }
  });
} else if (parsedData && parsedData.profilesDiscovered) {
  // Handle the expected format if the agent returns it
  discoveredProfiles = parsedData.profilesDiscovered;
  if (parsedData.discoveryMetadata) {
    metadata = { ...metadata, ...parsedData.discoveryMetadata };
  }
}

// If still no profiles, try to extract from raw text
if (Object.keys(discoveredProfiles).length === 0) {
  const text = JSON.stringify(agentOutput);

  // Extract URLs using regex
  const urlPatterns = {
    linkedin: /linkedin\.com\/in\/([a-zA-Z0-9-]+)/gi,
    twitter: /(?:twitter|x)\.com\/([a-zA-Z0-9_]+)/gi,
    github: /github\.com\/([a-zA-Z0-9-]+)/gi,
    instagram: /instagram\.com\/@?([a-zA-Z0-9_.]+)/gi,
    youtube: /youtube\.com\/(?:channel\/|@)?([a-zA-Z0-9_-]+)/gi,
    tiktok: /tiktok\.com\/@([a-zA-Z0-9_.]+)/gi
  };

  for (const [platform, pattern] of Object.entries(urlPatterns)) {
    const matches = text.matchAll(pattern);
    for (const match of matches) {
      if (!discoveredProfiles[platform]) {
        discoveredProfiles[platform] = {
          handle: match[1],
          url: `https://${match[0]}`,
          confidence: 0.5,
          markers: ["Found in search results"],
          profileData: {}
        };
        metadata.profilesFound++;
      }
    }
  }
}

// Update platform count
metadata.platformsChecked = Object.keys(discoveredProfiles).length;

// Build final output in the structure expected by the next nodes
const formattedOutput = {
  profilesDiscovered: discoveredProfiles,
  discoveryMetadata: metadata,
  verificationDetails: {
    crossPlatformLinks: [],
    commonIdentifiers: {},
    consistentData: {}
  },
  recommendedPrimaryProfiles: {},
  searchErrors: [],
  // Keep original input data for reference
  originalInput: {
    fullName: agentOutput.fullName || '',
    company: agentOutput.company || '',
    location: agentOutput.location || '',
    email: agentOutput.email || ''
  }
};

// Add verification details if we have multiple profiles
if (Object.keys(discoveredProfiles).length > 1) {
  // Extract common data across profiles
  const allNames = new Set();
  const allCompanies = new Set();
  const allLocations = new Set();

  Object.values(discoveredProfiles).forEach(profile => {
    if (profile.profileData) {
      if (profile.profileData.name) allNames.add(profile.profileData.name);
      if (profile.profileData.company) allCompanies.add(profile.profileData.company);
      if (profile.profileData.location) allLocations.add(profile.profileData.location);
    }
  });

  formattedOutput.verificationDetails.consistentData = {
    name: allNames.size === 1 ? Array.from(allNames)[0] : '',
    company: allCompanies.size === 1 ? Array.from(allCompanies)[0] : '',
    location: allLocations.size === 1 ? Array.from(allLocations)[0] : '',
    profilePhoto: false // Would need actual photo comparison
  };
}

// Add recommended profiles based on what was found
if (discoveredProfiles.linkedin) {
  formattedOutput.recommendedPrimaryProfiles.professional = {
    platform: 'linkedin',
    url: discoveredProfiles.linkedin.url,
    reason: 'Primary professional network profile'
  };
}

if (discoveredProfiles.twitter) {
  formattedOutput.recommendedPrimaryProfiles.social = {
    platform: 'twitter',
    url: discoveredProfiles.twitter.url,
    reason: 'Primary social media presence'
  };
}

if (discoveredProfiles.github) {
  formattedOutput.recommendedPrimaryProfiles.technical = {
    platform: 'github',
    url: discoveredProfiles.github.url,
    reason: 'Primary technical/developer profile'
  };
}

if (discoveredProfiles.youtube) {
  formattedOutput.recommendedPrimaryProfiles.content = {
    platform: 'youtube',
    url: discoveredProfiles.youtube.url,
    reason: 'Content creation and thought leadership'
  };
}

// Add summary statistics
formattedOutput.summary = {
  totalProfilesFound: metadata.profilesFound,
  highConfidenceMatches: metadata.highConfidenceProfiles,
  platformsCovered: Object.keys(discoveredProfiles),
  primaryProfile: formattedOutput.recommendedPrimaryProfiles.professional || 
                  formattedOutput.recommendedPrimaryProfiles.social || 
                  formattedOutput.recommendedPrimaryProfiles.technical ||
                  null
};

return formattedOutput;

// Get discovered profiles from previous node
const discoveryOutput = $input.item.json;
const profiles = discoveryOutput.profilesDiscovered || {};

// Initialize validation results
const validationResults = {
  profilesValidated: {},
  crossPlatformVerification: {
    nameConsistency: 0,
    locationConsistency: 0,
    companyConsistency: 0,
    overallConsistency: 0
  },
  confidenceScores: {},
  verificationMatrix: {},
  primaryIdentity: {
    name: '',
    company: '',
    location: '',
    email: '',
    primaryPlatform: ''
  },
  validationFlags: [],
  suggestedActions: []
};

// Extract all unique values across profiles
const names = new Set();
const companies = new Set();
const locations = new Set();
const bios = [];
const highestConfidenceProfile = { confidence: 0, platform: '' };

// Analyze each profile
Object.entries(profiles).forEach(([platform, profile]) => {
  if (!profile || !profile.url) return;

  // Track confidence scores
  validationResults.confidenceScores[platform] = profile.confidence || 0.5;

  // Track highest confidence profile
  if (profile.confidence > highestConfidenceProfile.confidence) {
    highestConfidenceProfile.confidence = profile.confidence;
    highestConfidenceProfile.platform = platform;
  }

  // Extract identity markers
  if (profile.profileData) {
    if (profile.profileData.name) names.add(profile.profileData.name);
    if (profile.profileData.company) companies.add(profile.profileData.company);
    if (profile.profileData.location) locations.add(profile.profileData.location);
    if (profile.profileData.bio) bios.push({ platform, bio: profile.profileData.bio });
  }

  // Build validated profile entry
  validationResults.profilesValidated[platform] = {
    ...profile,
    validationStatus: profile.confidence >= 0.7 ? 'verified' : 
                      profile.confidence >= 0.5 ? 'probable' : 'uncertain',
    validationNotes: []
  };
});

// Calculate consistency scores
validationResults.crossPlatformVerification.nameConsistency = 
  names.size <= 1 ? 1.0 : 0.5 / names.size;
validationResults.crossPlatformVerification.companyConsistency = 
  companies.size <= 1 ? 1.0 : 0.5 / companies.size;
validationResults.crossPlatformVerification.locationConsistency = 
  locations.size <= 1 ? 1.0 : 0.5 / locations.size;

// Overall consistency score
validationResults.crossPlatformVerification.overallConsistency = 
  (validationResults.crossPlatformVerification.nameConsistency + 
   validationResults.crossPlatformVerification.companyConsistency + 
   validationResults.crossPlatformVerification.locationConsistency) / 3;

// Determine primary identity based on highest confidence profile
if (highestConfidenceProfile.platform && profiles[highestConfidenceProfile.platform]) {
  const primaryProfile = profiles[highestConfidenceProfile.platform];
  validationResults.primaryIdentity = {
    name: Array.from(names)[0] || discoveryOutput.originalInput?.fullName || '',
    company: Array.from(companies)[0] || discoveryOutput.originalInput?.company || '',
    location: Array.from(locations)[0] || discoveryOutput.originalInput?.location || '',
    email: discoveryOutput.originalInput?.email || '',
    primaryPlatform: highestConfidenceProfile.platform
  };
}

// Create verification matrix (which platforms link to each other)
Object.entries(profiles).forEach(([platform1, profile1]) => {
  validationResults.verificationMatrix[platform1] = {};

  Object.entries(profiles).forEach(([platform2, profile2]) => {
    if (platform1 === platform2) {
      validationResults.verificationMatrix[platform1][platform2] = 1.0;
    } else {
      // Check for cross-references in bios or markers
      let linkScore = 0;

      // Check if bio mentions other platform
      if (profile1.profileData?.bio && 
          profile1.profileData.bio.toLowerCase().includes(platform2.toLowerCase())) {
        linkScore += 0.5;
      }

      // Check if markers mention cross-platform links
      if (profile1.markers) {
        profile1.markers.forEach(marker => {
          if (marker.toLowerCase().includes(platform2.toLowerCase())) {
            linkScore += 0.3;
          }
        });
      }

      validationResults.verificationMatrix[platform1][platform2] = Math.min(linkScore, 1.0);
    }
  });
});

// Add validation flags based on findings
if (validationResults.crossPlatformVerification.overallConsistency < 0.7) {
  validationResults.validationFlags.push({
    type: 'warning',
    message: 'Inconsistent information across profiles - manual review recommended',
    severity: 'medium'
  });
}

if (names.size > 1) {
  validationResults.validationFlags.push({
    type: 'info',
    message: `Multiple name variations found: ${Array.from(names).join(', ')}`,
    severity: 'low'
  });
}

if (Object.keys(profiles).length < 2) {
  validationResults.validationFlags.push({
    type: 'info',
    message: 'Limited profiles found for cross-verification',
    severity: 'low'
  });
}

// Suggest actions for improving validation
if (Object.keys(profiles).length < 3) {
  validationResults.suggestedActions.push('Search additional platforms for better cross-verification');
}

Object.entries(validationResults.confidenceScores).forEach(([platform, score]) => {
  if (score < 0.7) {
    validationResults.suggestedActions.push(`Manual verification recommended for ${platform} profile`);

    // Add specific validation notes
    if (validationResults.profilesValidated[platform]) {
      validationResults.profilesValidated[platform].validationNotes.push(
        'Low confidence score - additional verification needed'
      );
    }
  }
});

// Calculate trust score for the entire profile set
const avgConfidence = Object.values(validationResults.confidenceScores)
  .reduce((sum, score) => sum + score, 0) / Object.keys(validationResults.confidenceScores).length;

validationResults.overallTrustScore = 
  (avgConfidence * 0.5) + 
  (validationResults.crossPlatformVerification.overallConsistency * 0.3) +
  (Object.keys(profiles).length / 10 * 0.2); // More platforms = higher trust

// Add metadata
validationResults.validationMetadata = {
  timestamp: new Date().toISOString(),
  profilesAnalyzed: Object.keys(profiles).length,
  highConfidenceProfiles: Object.values(validationResults.confidenceScores)
    .filter(score => score >= 0.8).length,
  validationMethod: 'cross-platform-analysis',
  trustLevel: validationResults.overallTrustScore >= 0.8 ? 'high' :
              validationResults.overallTrustScore >= 0.6 ? 'medium' : 'low'
};

// Pass through all original data plus validation results
return {
  ...discoveryOutput,
  validationResults,
  profilesValidated: validationResults.profilesValidated,
  primaryIdentity: validationResults.primaryIdentity,
  trustScore: validationResults.overallTrustScore,
  proceedToAnalysis: validationResults.overallTrustScore >= 0.5 // Only proceed if trust is sufficient
};

// Get the validated profiles from previous node
const validatedData = $input.item.json;
const profiles = validatedData.profilesDiscovered || {};

// Create an array to hold separate items for each platform
const platformItems = [];

// Process each discovered platform
Object.entries(profiles).forEach(([platform, profileData]) => {
  if (!profileData || !profileData.url) return;

  // Create an item for each platform
  platformItems.push({
    json: {
      platform: platform.toLowerCase(),
      profileUrl: profileData.url,
      handle: profileData.handle,
      confidence: profileData.confidence,
      markers: profileData.markers,
      profileData: profileData.profileData,
      searchDepth: validatedData.searchConfig?.depth || 'comprehensive',
      analysisDepth: validatedData.searchConfig?.depth || 'comprehensive',
      originalInput: validatedData.originalInput || {},
      primaryIdentity: validatedData.primaryIdentity || {},
      trustScore: validatedData.trustScore || 0.5
    }
  });
});

// Return all platform items for processing
return platformItems;

