// Inputs expected from Aggregate/RSS: arrays: title[], content[], link[]
const all = items[0].json;              // aggregated item
const titles = all.title || [];
const contents = all.content || [];
const links = all.link || [];

const articles = [];
const lines = [];

const maxLen = Math.max(titles.length, contents.length, links.length);
for (let i = 0; i < maxLen; i++) {
  const obj = {
    id: i + 1,                          // stable ID (1..N). Use guid if you have one.
    title: titles[i] ?? "",
    content: contents[i] ?? "",
    link: links[i] ?? ""
  };
  articles.push(obj);
  // Short preview for the AI to choose from:
  const preview = (obj.content || "").replace(/\s+/g, " ").slice(0, 220);
  lines.push(`${obj.id}. ${obj.title}\n${preview}${preview.length === 220 ? "‚Ä¶" : ""}`);
}

return [
  {
    json: {
      articles,                         // keep the structured mapping
      forAI: lines.join("\n\n")         // human-readable list for the model
    }
  }
];

// Code node ‚Äî Run Once for All Items

// 1) Read AI output text (your AI Agent uses `output`)
let aiText =
  (items[0]?.json?.output ??
   items[0]?.json?.result ??
   items[0]?.json?.text ??
   items[0]?.json?.response ??
   "").toString().trim();

// Fallback: read directly from the AI Agent node by name if not passed through
if (!aiText) {
  const ai = $items("Best Article finder", 0, 0);   // <-- change to your AI node name if different
  if (Array.isArray(ai) && ai.length) {
    aiText = (ai[0].json.output ?? ai[0].json.result ?? ai[0].json.text ?? "").toString().trim();
  }
}

if (!aiText) {
  throw new Error("AI output is empty. Make sure the AI Agent node's field name is 'output' (or adjust above).");
}

// 2) Get articles array from builder node
let articles = items[0]?.json?.articles;
if (!articles) {
  const built = $items("group the news into 1 item", 0, 0);   // <-- change "Code1" to your builder node name
  if (Array.isArray(built) && built.length) {
    articles = built[0]?.json?.articles;
  }
}
if (!Array.isArray(articles)) {
  throw new Error("Couldn't find 'articles' array. Ensure the builder node outputs { json: { articles: [...] } }.");
}

// 3) Extract numeric IDs (handles commas, spaces, newlines, bullets)
const ids = (aiText.match(/\d+/g) || []).map(n => parseInt(n, 10));

// 4) Map IDs ‚Üí full article objects
const chosen = ids
  .map(id => articles.find(a => a.id === id))
  .filter(Boolean);

// 5) Return one item with arrays
return [{
  json: {
    raw: aiText,
    ids,                                  // e.g., [2,18,45]
    chosen,                                // [{id, title, content, link}, ...]
    titles: chosen.map(c => c.title),
    links: chosen.map(c => c.link)
  }
}];

// Code node ‚Äî Run Once for All Items

// Collect all links from input items
const links = [];
for (const item of $input.all()) {
  if (item.json.link) {
    links.push(item.json.link);
  }
  // if articles are inside "chosen" array:
  if (Array.isArray(item.json.chosen)) {
    for (const c of item.json.chosen) {
      if (c.link) links.push(c.link);
    }
  }
}

// Return as one item with all links
return [
  {
    json: {
      links,                          // array of links
      linksString: links.join("\n")   // optional: single string
    }
  }
];
