// Extract and structure meeting minutes data from webhook
// Parse the JSON body if it's a string, otherwise use the object directly
let inputData;
try {
  const rawInput = $input.first().json;

  // Check if body exists and is a string that needs parsing
  if (rawInput.body && typeof rawInput.body === 'string') {
    inputData = JSON.parse(rawInput.body);
  } else if (rawInput.body && typeof rawInput.body === 'object') {
    inputData = rawInput.body;
  } else {
    // Fall back to the raw input data
    inputData = rawInput;
  }
} catch (error) {
  // If parsing fails, use the original data
  inputData = $input.first().json.body || $input.first().json;
}

// Basic meeting information
const meetingInfo = {
  date: inputData.date || '',
  time: inputData.time || '',
  location: inputData.location || '',
  submittedAt: inputData.submitted_at || '',
  userAgent: inputData.user_agent || ''
};

// Text cleaning function
const cleanText = (text) => {
  if (typeof text !== 'string') return text;
  return text
    .replace(/\r\n/g, ' ')  // Replace Windows line breaks
    .replace(/\n/g, ' ')    // Replace line breaks
    .replace(/\r/g, ' ')    // Replace carriage returns  
    .replace(/\t/g, ' ')    // Replace tabs
    .trim();
};

// Format participants with semicolon separators
const attendeesFormatted = inputData.attendees 
  ? inputData.attendees.split('\n')
      .map(name => name.trim())
      .filter(name => name.length > 0)
      .join(';')
  : '';

const absenteesFormatted = inputData.absentees 
  ? inputData.absentees.split('\n')
      .map(name => name.trim())
      .filter(name => name.length > 0)
      .join(';')
  : '';

// Format discussion points with titles and comma separators
const discussionPointsFormatted = (inputData.dps || [])
  .filter(dp => dp.title || dp.decision || dp.notes)
  .map((dp, index) => {
    const parts = [];
    if (dp.title) parts.push(`Title: ${dp.title}`);
    if (dp.decision) parts.push(`Decision: ${dp.decision}`);
    if (dp.notes) parts.push(`Notes: ${dp.notes}`);
    return parts.join(', ');
  })
  .join(';');

// Format action items with titles and comma separators
const actionItemsFormatted = (inputData.ais || [])
  .filter(ai => ai.action || ai.owner || ai.deadline)
  .map((ai, index) => {
    const parts = [];
    if (ai.action) parts.push(`Action: ${ai.action}`);
    if (ai.owner) parts.push(`Owner: ${ai.owner}`);
    if (ai.deadline) parts.push(`Deadline: ${ai.deadline}`);
    return parts.join(', ');
  })
  .join(';');

// Statistics
const statistics = {
  totalAttendees: inputData.attendees ? inputData.attendees.split('\n').filter(n => n.trim()).length : 0,
  totalAbsentees: inputData.absentees ? inputData.absentees.split('\n').filter(n => n.trim()).length : 0,
  totalDiscussionPoints: (inputData.dps || []).filter(dp => dp.title || dp.decision || dp.notes).length,
  totalActionItems: (inputData.ais || []).filter(ai => ai.action || ai.owner || ai.deadline).length
};

// Add debug logging to help troubleshoot
console.log('Input Data Keys:', Object.keys(inputData || {}));
console.log('Attendees raw:', inputData.attendees);
console.log('Discussion Points:', inputData.dps);
console.log('Action Items:', inputData.ais);

// Return formatted data with cleaned text
return [{
  json: {
    // Basic info
    date: meetingInfo.date,
    time: meetingInfo.time,
    location: cleanText(meetingInfo.location),

    // Formatted participants (semicolon separated)
    attendees: attendeesFormatted,
    absentees: absenteesFormatted,

    // Meeting content (cleaned)
    opening: cleanText(inputData.opening || ''),
    aob: cleanText(inputData.aob || ''),
    closing: cleanText(inputData.closing || ''),

    // Formatted discussion points (semicolon separated, comma within each point)
    discussionPoints: discussionPointsFormatted,

    // Formatted action items (semicolon separated, comma within each item)
    actionItems: actionItemsFormatted,

    // Statistics
    statistics: statistics,

    // Metadata
    submittedAt: meetingInfo.submittedAt,
    userAgent: meetingInfo.userAgent,

    // Debug info (remove in production)
    _debug: {
      inputType: typeof inputData,
      hasBody: !!$input.first().json.body,
      bodyType: typeof $input.first().json.body,
      inputKeys: Object.keys(inputData || {})
    }
  }
}];
