parameters.inputSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "type": "object",
  "properties": {
    "items": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "Step": {
            "type": "array",
            "description": "The unique id of the step that is reviewed.",
            "items": { "type": "integer", "minimum": 1 },
            "minItems": 1,
            "maxItems": 1
          },
          "Proposed action": {
            "type": "string",
            "enum": ["Keep", "Update", "Split", "Merge", "Delete"],
            "description": "The action that needs to be taken for the step."
          },
          "Reason": {
            "type": "string"
          },
          "Feedback summary": {
            "type": "string"
          },
          "Updated name": {
            "type": "string",
            "description": "A new name for the existing step."
          },
          "Updated checklist": {
            "type": "string",
            "description": "A new checklist for the existing step."
          },
          "Steps to merge": {
            "type": "array",
            "description": "The unique id's of the two steps that need to be merged.",
            "items": { "type": "integer", "minimum": 2 },
            "minItems": 2,
            "maxItems": 2
          },
          "Merged name": {
            "type": "string",
            "description": "The new name for the merged step."
          },
          "Merged checklist": {
            "type": "string",
            "description": "The new checklist for the merged step."
          },
          "New step 1 name": {
            "type": "string",
            "description": "A name for the first new step."
          },
          "New step 1 checklist": {
            "type": "string",
            "description": "The checklist for the first new step."
          },
          "New step 2 Name": {
            "type": "string",
            "description": "A name for the second new step."
          },
          "New step 2 checklist": {
            "type": "string",
            "description": "The checklist for the second new step."
          }
        },
        "required": [
          "Step",
          "Proposed action",
          "Reason",
          "Feedback summary"
        ],
        "additionalProperties": false,
        "allOf": [
          {
            "if": { "properties": { "Proposed action": { "const": "Keep" } } },
            "then": {
              "required": ["Step", "Proposed action", "Reason", "Feedback summary", "Updated name", "Updated checklist"],
              "not": { "required": ["Steps to merge", "Merged name", "Merged checklist","New step 1 name", "New step 1 checklist", "New step 2 name", "New step 2 checklist"] }
            }
          },
          {
            "if": { "properties": { "Proposed action": { "const": "Merge" } } },
            "then": {
              "required": ["Step", "Proposed action", "Reason", "Feedback summary", "Steps to merge", "Merged name", "Merged checklist"],
              "not": { "required": ["Updated name", "Updated checklist","New step 1 name", "New step 1 checklist", "New step 2 name", "New step 2 checklist"] }
            }
          },
          {
            "if": { "properties": { "Proposed action": { "const": "Split" } } },
            "then": {
              "required": ["Step", "Proposed action", "Reason", "Feedback summary", "New step 1 name", "New step 1 checklist", "New step 2 name", "New step 2 checklist"],
              "not": { "required": ["Updated name", "Updated checklist", "Steps to merge", "Merged name", "Merged checklist"] }
            }
          }
        ]
      }
    }
  },
  "required": ["items"],
  "additionalProperties": false
}
