// INPUTS
const html = $json.data;                                      // HTML complet
const articleUrl = $('Split Out').item.json.urls || "";       // URL de l‚Äôarticle (pour les URLs relatives)

// ------------------ Utils ------------------
function decodeEntities(text) {
  return text
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&#039;/g, "'")
    .replace(/&quot;/g, '"')
    .replace(/&nbsp;/g, ' ')
    .replace(/&eacute;/g, '√©')
    .replace(/&egrave;/g, '√®')
    .replace(/&agrave;/g, '√†');
}

function resolveUrl(baseUrl, src) {
  if (!src) return null;
  // //cdn‚Ä¶ ‚Üí https:
  if (src.startsWith('//')) return 'https:' + src;
  // absolute
  try {
    return new URL(src, baseUrl).href;
  } catch {
    return src;
  }
}

// Supprime blocs connus de ‚Äúparasites‚Äù (share, related, newsletter, footer‚Ä¶)
function stripNoise(htmlChunk) {
  let s = htmlChunk;

  // blocs ‚Äúshare/social‚Äù
  s = s.replace(/<aside[\s\S]*?<\/aside>/gi, '');
  s = s.replace(/<section[^>]*?(?:share|social|related|newsletter|comments)[^>]*>[\s\S]*?<\/section>/gi, '');
  s = s.replace(/<div[^>]*?(?:share|social|related|newsletter|comments|social-tools|meta-social)[^>]*>[\s\S]*?<\/div>/gi, '');

  // scripts/styles
  s = s.replace(/<script[\s\S]*?<\/script>/gi, '');
  s = s.replace(/<style[\s\S]*?<\/style>/gi, '');

  // nav/footer/header souvent non-article
  s = s.replace(/<(?:nav|footer|header)[\s\S]*?<\/(?:nav|footer|header)>/gi, '');

  return s;
}

// Extrait tous les <p> d‚Äôun bloc HTML (en gardant l‚Äôordre)
function extractParagraphs(htmlChunk) {
  const ps = [];
  const re = /<p[^>]*>([\s\S]*?)<\/p>/gi;
  let m;
  while ((m = re.exec(htmlChunk)) !== null) {
    // retire tags r√©siduels dans <p>
    let t = m[1]
      .replace(/<figure[\s\S]*?<\/figure>/gi, '')
      .replace(/<[^>]+>/g, '')
      .replace(/\s+/g, ' ')
      .trim();
    if (t) ps.push(t);
  }
  return ps;
}

// Cherche une image principale : og:image > twitter:image > figure > img dans article/main
function findMainImage(html, baseUrl) {
  let imageMatch =
    html.match(/<meta[^>]+property=["']og:image["'][^>]+content=["']([^"']+)["']/i) ||
    html.match(/<meta[^>]+name=["']twitter:image["'][^>]+content=["']([^"']+)["']/i);

  if (imageMatch) return resolveUrl(baseUrl, imageMatch[1]);

  // image dans <figure>
  imageMatch =
    html.match(/<figure[\s\S]*?<img[^>]+src=["']([^"']+)["'][\s\S]*?<\/figure>/i) ||
    html.match(/<(?:article|main)[^>]*>[\s\S]*?<img[^>]+src=["']([^"']+)["']/i) ||
    html.match(/<img[^>]+src=["']([^"']+)["']/i);

  return imageMatch ? resolveUrl(baseUrl, imageMatch[1]) : null;
}

// ------------------ Titre ------------------
let titleMatch =
  html.match(/<meta[^>]+property=["']og:title["'][^>]+content=["']([^"']+)["']/i) ||
  html.match(/<meta[^>]+name=["']twitter:title["'][^>]+content=["']([^"']+)["']/i) ||
  html.match(/<title>([\s\S]*?)<\/title>/i);
let title = titleMatch ? titleMatch[1] : "";

// ------------------ Corps de l‚Äôarticle ------------------
// 1) Privil√©gie <article>, sinon <main>, sinon grands conteneurs classiques
const candidatePatterns = [
  /<article[^>]*>([\s\S]*?)<\/article>/i,
  /<main[^>]*>([\s\S]*?)<\/main>/i,
  /<div[^>]+class=["'][^"']*(?:article__body|article-body|articleBody|content-article|story|post-content|post__content|entry-content|main-content|content__body)[^"']*["'][^>]*>([\s\S]*?)<\/div>/i,
  // fallback : gros container central (layout courant)
  /<div[^>]+class=["'][^"']*(?:container|content|wrapper)[^"']*["'][^>]*>([\s\S]*?)<\/div>/i,
];

let articleHtml = "";
for (const p of candidatePatterns) {
  const m = html.match(p);
  if (m && m[1]) {
    articleHtml = m[1];
    break;
  }
}
// Si encore vide, on prend tout le HTML (on filtrera)
if (!articleHtml) articleHtml = html;

// 2) Nettoyage ‚Äúbruit‚Äù (share, related, scripts, etc.)
articleHtml = stripNoise(articleHtml);

// 3) R√©cup√®re beaucoup plus de texte : on concat√®ne les <p>
let paragraphs = extractParagraphs(articleHtml);

// Si on n‚Äôa quasiment rien, tente aussi les <h2>/<h3> comme ‚Äúparagraphes‚Äù
if (paragraphs.length < 3) {
  const hx = [];
  const reHx = /<(?:h2|h3)[^>]*>([\s\S]*?)<\/(?:h2|h3)>/gi;
  let m;
  while ((m = reHx.exec(articleHtml)) !== null) {
    const t = m[1].replace(/<[^>]+>/g, '').replace(/\s+/g, ' ').trim();
    if (t) hx.push(t);
  }
  paragraphs = paragraphs.concat(hx);
}

// 4) Filtrage l√©ger : on retire les lignes trop courtes (menus, miettes)
paragraphs = paragraphs.filter(p => p.length > 40);

// 5) Limites souples (√©vite d‚Äôavaler l‚Äôentier du site)
const MAX_PARAGRAPHS = 20;     // augmente si tu veux encore plus
const MAX_CHARS      = 8000;   // coupe si trop long
let content = "";
for (const p of paragraphs.slice(0, MAX_PARAGRAPHS)) {
  if ((content + p + "\n\n").length > MAX_CHARS) break;
  content += p + "\n\n";
}
content = content.trim();

// 6) Coupe les blocs ‚Äúparasites‚Äù s‚Äôils apparaissent *plus loin* dans l‚Äôarticle
const breakMarkers = [
  "Partager", "Commenter", "Partagez sur", "Lire aussi", "Sur le m√™me sujet",
  "Voir aussi", "Suivez-nous", "Retrouvez-nous", "Contactez", "R√©daction",
  "lire plus tard", "newsletter", "publicit√©", "nos autres articles"
];
// coupe uniquement apr√®s un petit seuil pour ne pas tronquer l‚Äôintro
for (const marker of breakMarkers) {
  const idx = content.indexOf(marker);
  if (idx > 300) {
    content = content.slice(0, idx).trim();
    break;
  }
}

// ------------------ Image principale ------------------
let imageUrl = findMainImage(html, articleUrl);

// ------------------ Finalisation ------------------
title   = decodeEntities(title);
content = decodeEntities(content);

return {
  json: {
    title,
    urls: articleUrl,
    content,
    imageUrl
  }
};

