// Data Cleaner Node - Fixed Version
const inputData = $input.all();
console.log('=== Data Cleaner Node Started ===');
console.log('Input data type:', typeof inputData);
console.log('Input data length:', inputData ? inputData.length : 'undefined');
console.log('Input data structure:', JSON.stringify(inputData, null, 2));

const cleanedData = [];

// Ensure there is input data
if (!inputData || inputData.length === 0) {
  console.log('No input data');
  return [];
}

// Process each input item
for (let i = 0; i < inputData.length; i++) {
  const item = inputData[i];
  console.log(`\nProcessing item ${i + 1}:`, item);

  try {
    // Extract actual data content
    let newsData;

    // Check different data structures
    if (item.output) {
      // Standard format: {output: {...}}
      newsData = item.output;
      console.log('Found output format data');
    } else if (item.json && item.json.output) {
      // n8n format: {json: {output: {...}}}
      newsData = item.json.output;
      console.log('Found json.output format data');
    } else if (item.json) {
      // Direct json format: {json: {...}}
      newsData = item.json;
      console.log('Found direct json format data');
    } else {
      // Direct data format
      newsData = item;
      console.log('Found direct data format');
    }

    console.log('Extracted news data:', newsData);

    // Validate required fields
    if (!newsData.title || !newsData.pubDate) {
      console.log('Skipping invalid data item, missing required fields');
      continue;
    }

    // Clean and standardize data
    const cleanOutput = {
      title: (newsData.title || '').replace(/"/g, '').trim(),
      pubDate: (newsData.pubDate || '').trim(),
      summary: (newsData.summary || '').replace(/"/g, '').trim(),
      keywords: Array.isArray(newsData.keywords) ? newsData.keywords : [],
      rating: (newsData.rating || '').trim(),
      link: (newsData.link || '').trim()
    };

    console.log('Cleaned data:', cleanOutput);

    // Add to cleaned data array
    cleanedData.push({
      json: {
        output: cleanOutput
      }
    });

    console.log('âœ… Successfully processed data item:', cleanOutput.title.substring(0, 50));

  } catch (error) {
    console.log('âŒ Error processing data item:', error.message);
    console.log('Problematic data item:', item);
    continue;
  }
}

console.log('\n=== Data Cleaning Completed ===');
console.log('Cleaned data count:', cleanedData.length);
console.log('Cleaned data:', JSON.stringify(cleanedData, null, 2));

return cleanedData;
