const base64Data = $json.data[0].b64_json;

if (!base64Data) {
  throw new Error("KhÃ´ng tÃ¬m tháº¥y dá»¯ liá»‡u áº£nh base64.");
}

return [
  {
    json: {},
    binary: {
      data: {
        data: base64Data,
        mimeType: "image/png",
        fileName: "image.png"
      }
    }
  }
];

let rawText = $json.text || '';

// BÆ°á»›c 1: TÃ¡ch pháº§n JSON bÃªn trong ```json ... ```
const matched = rawText.match(/```json\s*([\s\S]*?)\s*```/);

if (!matched || !matched[1]) {
  return [{ json: { prompt_image: '', content: '', error: 'Can not find JSON in ```json```' } }];
}

let jsonString = matched[1]
  .replace(/\n/g, '')      // XoÃ¡ xuá»‘ng dÃ²ng
  .replace(/\r/g, '')      // XoÃ¡ carriage return
  .replace(/\t/g, '')      // XoÃ¡ tab (náº¿u cÃ³)
  .trim();

try {
  const parsed = JSON.parse(jsonString);
  return [{
    json: {
      prompt_image: parsed.prompt_image || '',
      content: parsed.content || ''
    }
  }];
} catch (err) {
  return [{
    json: {
      prompt_image: '',
      content: '',
      error: 'error parse JSON: ' + err.message
    }
  }];
}
