// n8n Function node
// Input: items[0].json.comments (array of comment objects)
// Output: one item per comment with { content: "..." }

const results = [];

for (const item of items) {
  if (item.json.comments && Array.isArray(item.json.comments)) {
    for (const comment of item.json.comments) {
      results.push({
        json: {
          content: comment.content
        }
      });
    }
  }
}

return results;

