import re

out_items = []

for item in items:
    text = item["json"].get("message", {}).get("text", "").strip()
    parts = text.split(maxsplit=1)

    if len(parts) == 2:
        correlation_id = parts[1].strip()
        # quick regex UUID check
        if not re.match(r'^[0-9a-fA-F-]{36}$', correlation_id):
            correlation_id = None
    else:
        correlation_id = None

    out_items.append({
        "json": {
            "correlation_id": correlation_id,
            "chat_id": item["json"]["message"]["chat"]["id"]
        }
    })

return out_items

