parameters.pythonCode:
import re

out_items = []

for item in items:
    text = item["json"].get("message", {}).get("text", "").strip()
    parts = text.split(maxsplit=2)

    if len(parts) == 3:
        correlation_id, new_status = parts[1], parts[2].lower()
        if not re.match(r'^[0-9a-fA-F-]{36}$', correlation_id):
            correlation_id = None
    else:
        correlation_id, new_status = None, None

    out_items.append({
        "json": {
            "correlation_id": correlation_id,
            "new_status": new_status,
            "chat_id": item["json"]["message"]["chat"]["id"]
        }
    })

return out_items

