parameters.jsCode:
const tickets = items;

let message = "üìã <b>Latest Tickets</b>\n\n";

tickets.forEach((row, i) => {
  message += `${i+1}. <b>ID:</b> ${row.json.correlation_id}\n`;
  message += `   <b>Status:</b> ${row.json.status}\n`;
  message += `   <i>${row.json.subject || "No subject"}</i>\n`;
  message += `   Created: ${row.json.created_at}\n\n`;
});

// pull original chat_id from the trigger
const chatId = $("01 Telegram Trigger: Intake + Status").item.json.message.from.id;

return [{ json: { text: message, chat_id: chatId } }];
