// Extract files from Parse Message and prepare them for batch processing
const parseMessageData = $('Parse Message into Jira Format').first().json;
const jiraIssueData = $('Create Jira Issue').first().json;

console.log('Parse Message Data:', JSON.stringify(parseMessageData, null, 2));
console.log('Jira Issue Data:', JSON.stringify(jiraIssueData, null, 2));

// Extract files from the attachments array (not files array)
const files = parseMessageData.attachments || [];
const jiraIssueKey = jiraIssueData.key;

console.log(`Found ${files.length} files to process`);
console.log('Jira Issue Key:', jiraIssueKey);

// Return each file as a separate item with the Jira issue key
const items = files.map((file, index) => {
  return {
    fileId: file.id,
    fileName: file.name || file.title || `file_${index}`,
    fileUrl: file.url, // Use the url property from your attachments
    jiraIssueKey: jiraIssueKey,
    fileIndex: index,
    totalFiles: files.length,
    originalFile: file
  };
});

console.log('Prepared items for batch processing:', JSON.stringify(items, null, 2));

return items;

