// Enhanced Parse Slack message - handles both plain text and rich text blocks
const message = $input.first().json;
let text = message.text || '';
const user = message.user || '';
const channel = message.channel || '';
const files = message.files || [];
const timestamp = message.ts || '';
const blocks = message.blocks || [];

console.log('Original text length:', text.length);
console.log('Blocks found:', blocks.length);
console.log('Files found:', files.length);

// Extract text from rich text blocks if available (this gives us complete text)
if (blocks && blocks.length > 0) {
  let richText = '';

  blocks.forEach(block => {
    if (block.type === 'rich_text' && block.elements) {
      block.elements.forEach(element => {
        if (element.type === 'rich_text_section' && element.elements) {
          element.elements.forEach(textElement => {
            if (textElement.type === 'text') {
              richText += textElement.text || '';
            }
          });
        } else if (element.type === 'rich_text_list' && element.elements) {
          // Handle bullet points and lists
          element.elements.forEach(listItem => {
            if (listItem.elements) {
              richText += '\nâ€¢ ';
              listItem.elements.forEach(listTextElement => {
                if (listTextElement.type === 'text') {
                  richText += listTextElement.text || '';
                }
              });
            }
          });
        }
      });
    }
  });

  // Use rich text if it's longer and contains more content
  if (richText.length > text.length) {
    console.log('Using rich text content (length:', richText.length, ')');
    text = richText;
  }
}

console.log('Final text length:', text.length);
console.log('Text preview:', text.substring(0, 200) + '...');

// Log file details for debugging
files.forEach((file, index) => {
  console.log(`File ${index}:`, {
    id: file.id,
    name: file.name,
    mimetype: file.mimetype,
    size: file.size,
    filetype: file.filetype
  });
});

// Robust regex patterns for title*:* format with flexible spacing
const titleMatch = text.match(/title\s*\*?\s*:\s*\*?\s*([^\n]*?)(?=\s*(?:\n|$))/i);

// For description, capture everything after description*:* until we hit priority: or type:
const descriptionMatch = text.match(/description\s*\*?\s*:\s*\*?\s*([\s\S]*?)(?=\s*priority\s*\*?\s*:\s*\*?|$)/i);

// Priority - capture just the value after priority:
const priorityMatch = text.match(/priority\s*\*?\s*:\s*\*?\s*([^\n]*?)(?=\s*(?:\n|$))/i);

// Type - capture just the value after type:
const typeMatch = text.match(/type\s*\*?\s*:\s*\*?\s*([^\n]*?)(?=\s*(?:\n|$))/i);

// Enhanced text cleaning function
const cleanText = (str) => {
  if (!str) return null;

  return str
    .replace(/^\s*[\*_\-\+=]*\s*/, '') // Remove leading special characters
    .replace(/\s*[\*_\-\+=]*\s*$/, '') // Remove trailing special characters
    .replace(/\*+/g, '') // Remove asterisks
    .replace(/_{2,}/g, '') // Remove multiple underscores
    .replace(/^\s+/gm, '') // Remove leading whitespace from lines
    .replace(/\s+$/gm, '') // Remove trailing whitespace from lines
    .replace(/\n\s*\n+/g, '\n\n') // Normalize multiple newlines
    .trim();
};

// Clean and extract values
const extractedTitle = titleMatch ? cleanText(titleMatch[1]) : null;
const extractedDescription = descriptionMatch ? cleanText(descriptionMatch[1]) : null;
const extractedPriority = priorityMatch ? cleanText(priorityMatch[1]) : null;
const extractedType = typeMatch ? cleanText(typeMatch[1]) : null;

// Enhanced logging
console.log('=== EXTRACTION RESULTS ===');
console.log('Title match:', !!titleMatch, '- Value:', extractedTitle);
console.log('Description match:', !!descriptionMatch, '- Length:', extractedDescription ? extractedDescription.length : 0);
console.log('Priority match:', !!priorityMatch, '- Value:', extractedPriority);
console.log('Type match:', !!typeMatch, '- Value:', extractedType);

// Smart fallback logic
const summary = (extractedTitle && extractedTitle.length > 3) ? 
               extractedTitle : 'Issue reported from Slack';

const description = (extractedDescription && extractedDescription.length > 5) ? 
                   extractedDescription : 'No description provided';

// Enhanced priority parsing
const determinePriority = (priorityText) => {
  if (!priorityText) return 'medium';

  const lowerPriority = priorityText.toLowerCase().trim();
  const cleanPriority = lowerPriority.replace(/[^\w\s]/g, '').trim();

  if (['critical', 'urgent', 'highest', 'blocker'].includes(cleanPriority)) return 'critical';
  if (['high'].includes(cleanPriority)) return 'high';
  if (['medium', 'med', 'normal', 'moderate'].includes(cleanPriority)) return 'medium';
  if (['low', 'lowest', 'minor'].includes(cleanPriority)) return 'low';

  if (cleanPriority.includes('critical') || cleanPriority.includes('urgent')) return 'critical';
  if (cleanPriority.includes('high')) return 'high';
  if (cleanPriority.includes('low')) return 'low';

  return 'medium';
};

// Enhanced issue type parsing
const determineIssueType = (typeText) => {
  if (!typeText) return 'enhancement';

  const lowerType = typeText.toLowerCase().trim();
  const cleanType = lowerType.replace(/[^\w\s]/g, '').trim();

  const typeMapping = {
    'bug': 'bug', 'issue': 'bug', 'defect': 'bug', 'error': 'bug', 'problem': 'bug',
    'feature': 'feature', 'story': 'feature', 'request': 'feature', 'new': 'feature',
    'enhancement': 'enhancement', 'improvement': 'enhancement', 'modify': 'enhancement', 
    'change': 'enhancement', 'update': 'enhancement', 'enhance': 'enhancement',
    'task': 'task', 'todo': 'task', 'work': 'task', 'chore': 'task',
    'epic': 'epic'
  };

  if (typeMapping[cleanType]) return typeMapping[cleanType];

  for (const [key, value] of Object.entries(typeMapping)) {
    if (cleanType.includes(key)) return value;
  }

  return 'enhancement';
};

const priority = determinePriority(extractedPriority);
const issueType = determineIssueType(extractedType);

// Map to Jira values with IDs
const priorityMapping = {
  'critical': { name: 'Highest', id: '1' },
  'high': { name: 'High', id: '2' },
  'medium': { name: 'Medium', id: '3' },
  'low': { name: 'Low', id: '4' }
};
const jiraPriorityObj = priorityMapping[priority] || priorityMapping['medium'];
const jiraPriority = jiraPriorityObj.name;
const jiraPriorityId = jiraPriorityObj.id;

const issueTypeMapping = {
  'bug': '10041',
  'feature': '10004',
  'enhancement': '10008',
  'task': '10008',
  'epic': '10000'
};
const jiraIssueType = issueTypeMapping[issueType] || '10008';

// Enhanced file processing
const attachments = files.map(file => {
  let downloadUrl = null;

  if (file.url_private_download && file.url_private_download.startsWith('https://')) {
    downloadUrl = file.url_private_download;
  } else if (file.url_private && file.url_private.startsWith('https://')) {
    downloadUrl = file.url_private;
  } else if (file.permalink && file.permalink.startsWith('https://')) {
    downloadUrl = file.permalink;
  } else if (file.url_download && file.url_download.startsWith('https://')) {
    downloadUrl = file.url_download;
  }

  const isVideo = file.mimetype && file.mimetype.startsWith('video/') || 
                  ['mp4', 'avi', 'mov', 'wmv', 'flv', 'webm', 'mkv'].includes(file.filetype?.toLowerCase());
  const isLargeFile = file.size && file.size > 10485760;

  return {
    id: file.id || 'unknown',
    name: file.name || file.title || 'unnamed_file',
    url: downloadUrl,
    size: file.size || 0,
    mimetype: file.mimetype || 'application/octet-stream',
    filetype: file.filetype || 'unknown',
    isVideo: isVideo,
    isLargeFile: isLargeFile,
    sizeFormatted: file.size ? `${(file.size / 1024 / 1024).toFixed(2)} MB` : 'Unknown size'
  };
}).filter(att => att.url);

// Create clean Jira description
let jiraDescription = '';

if (extractedDescription) {
  jiraDescription = extractedDescription + '\n\n';
}

// Add metadata
jiraDescription += `**Reported by:** <@${user}>\n`;
jiraDescription += `**Channel:** <#${channel}>\n`;
jiraDescription += `**Timestamp:** ${new Date(parseFloat(timestamp) * 1000).toISOString()}`;

// Add attachments info if present
if (attachments.length > 0) {
  jiraDescription += '\n\n**Attachments:**\n';
  attachments.forEach((att, index) => {
    jiraDescription += `${index + 1}. ${att.name} (${att.sizeFormatted})`;
    if (att.isVideo) jiraDescription += ' [VIDEO]';
    if (att.isLargeFile) jiraDescription += ' [LARGE FILE]';
    jiraDescription += '\n';
  });
}

return {
  summary,
  description: jiraDescription,
  priority: jiraPriority,
  priorityId: jiraPriorityId,
  issueType: jiraIssueType,
  issueTypeName: issueType,
  reporter: user,
  attachments,
  originalText: text,

  // Clear extraction results
  extractedData: {
    titleMatch: extractedTitle,
    descriptionMatch: extractedDescription,
    priorityMatch: extractedPriority,
    typeMatch: extractedType,

    // Debugging info
    parsing: {
      titleFound: !!titleMatch,
      descriptionFound: !!descriptionMatch,
      priorityFound: !!priorityMatch,
      typeFound: !!typeMatch,
      titleLength: extractedTitle ? extractedTitle.length : 0,
      descriptionLength: extractedDescription ? extractedDescription.length : 0,
      usedRichText: blocks.length > 0
    }
  },

  files: {
    totalFiles: files.length,
    validAttachments: attachments.length,
    videoFiles: attachments.filter(att => att.isVideo).length,
    largeFiles: attachments.filter(att => att.isLargeFile).length,
    attachmentDetails: attachments.map(att => ({
      name: att.name,
      size: att.sizeFormatted,
      type: att.filetype,
      isVideo: att.isVideo,
      isLarge: att.isLargeFile
    }))
  }
};
