const SP = 'customfield_10016';
const SPRINT = 'customfield_10020';

const items = $input.all();

const out = items.map(i => {
  const it = i.json || {};
  const f = it.fields || {};
  const sprintObj = Array.isArray(f[SPRINT]) ? f[SPRINT][0] : f[SPRINT]; // souvent tableau

  return {
    key: it.key,
    summary: f.summary || '',
    status: f.status?.name || '—',
    statusCategory: f.status?.statusCategory?.name || '—',
    assignee: f.assignee?.displayName || 'Unassigned',
    priority: f.priority?.name || 'Undefined',
    sp: Number(f[SP] || 0),

    sprint: sprintObj?.name || '—',
    sprintStart: sprintObj?.startDate || null,
    sprintEnd: sprintObj?.endDate || null,

    created: f.created || null,
    updated: f.updated || null,
    labels: f.labels || [],
  };
});

return out.map(x => ({ json: x }));

const rows = $input.all().map(i => i.json);
if (!rows.length) return [{ json: { isEmpty: true, message: 'Aucun ticket' } }];

const low = s => (s||'').toLowerCase();

const total = rows.length;
const done = rows.filter(r => low(r.status) === 'done').length;
const inProgress = rows.filter(r => low(r.status) === 'in progress').length;
const toDo = rows.filter(r => low(r.status) === 'to do').length;

const spTotal = rows.reduce((a,r)=>a+(r.sp||0),0);
const spDone  = rows.filter(r=>low(r.status)==='done').reduce((a,r)=>a+(r.sp||0),0);

const blockers = rows.filter(r =>
  (r.priority||'').toLowerCase()==='highest' ||
  /blocked|impediment/i.test(r.status) ||
  (r.labels||[]).map(low).includes('blocked')
).length;

const firstWithSprint = rows.find(r => r.sprint && r.sprint !== '—');
const sprintName = firstWithSprint?.sprint || 'Sprint en cours';
const sprintEnd = firstWithSprint?.sprintEnd || null;

return [{
  json: {
    sprintName, sprintEnd,
    counts: { total, done, inProgress, toDo },
    storyPoints: { total: spTotal, done: spDone, rate: spTotal ? Math.round(100*spDone/spTotal) : 0 },
    blockers,
    issues: rows,
    reportDate: new Date().toLocaleDateString('fr-FR'),
    reportTime: new Date().toLocaleTimeString('fr-FR'),
  }
}];

const d = $json;

function row(t){
  return `<tr>
    <td><a href="https:/yourdomain.atlassian.net/browse/${t.key}">${t.key}</a></td>
    <td>${t.summary || ''}</td>
    <td>${t.status || ''}</td>
    <td>${t.assignee || 'Unassigned'}</td>
    <td>${t.priority || 'Undefined'}</td>
    <td style="text-align:right">${t.sp || 0}</td>
  </tr>`;
}

const table = `<table border="1" cellspacing="0" cellpadding="6" style="border-collapse:collapse;width:100%">
  <tr>
    <th>Key</th>
    <th>Summary</th>
    <th>Status</th>
    <th>Assignee</th>
    <th>Priority</th>
    <th>SP</th>
  </tr>
  ${(d.issues || []).map(row).join('')}
</table>`;

const header = `<p><b>Tickets</b>: ${d.counts.done}/${d.counts.total}
 — <b>Story Points</b>: ${d.storyPoints.done}/${d.storyPoints.total} (${d.storyPoints.rate}%)
 — <b>Blockers</b>: ${d.blockers}</p>`;

const meta = `<p style="color:#6b7280">
Generated on ${d.reportDate} ${d.reportTime}${
  d.sprintEnd ? ` — Planned end: ${new Date(d.sprintEnd).toLocaleDateString('en-GB')}` : ''
}</p>`;

return [{ json: { html: `<h2>Sprint Report — ${d.sprintName}</h2>${meta}${header}${table}` } }];
