// Simple working version for n8n
let html = `<table style="width: 100%">`;

// Try multiple ways to access the data (n8n versions vary)
const inputData = $json || $input.item?.json || $input.first()?.json;

console.log('Input data:', JSON.stringify(inputData, null, 2));

// Tavily returns results in a 'results' array
const articles = inputData?.results || inputData?.articles || [];

if (Array.isArray(articles) && articles.length > 0) {
  for (const article of articles) {
    html += `
      <tr>
        <td style="background-color: #f2f4f8; font-family: sans-serif; padding: 1em;">
          <div>
            <a style="display: block; margin-bottom: 10px; font-size: 1.2em; text-decoration: none; color: #0066cc;" href="${article.url || '#'}">${article.title || 'No title available'}</a>
            <p style="margin: 10px 0; color: #666; font-style: italic;">
              ${article.content || article.snippet || article.description || 'No description available'}
            </p>
            <div style="margin-top: 10px; font-size: 0.9em; color: #888;">
              ${article.published_date ? '<b>Published:</b> ' + article.published_date + '<br>' : ''}
              ${article.score ? '<b>Relevance:</b> ' + Math.round(article.score * 100) + '%' : ''}
            </div>
          </div>
        </td>
      </tr>
    `;
  }
} else {
  html += `
    <tr>
      <td style="background-color: #f2f4f8; font-family: sans-serif; padding: 2em; text-align: center;">
        <p style="color: #666; font-style: italic;">No recent news found for this company.</p>
      </td>
    </tr>
  `;
}

html += '</table>';

return { html: html };
