// Prepare data for Google Sheets logging
const inputData = $input.first().json;
const ticketData = $node["Create Zendesk Ticket"].json;

// Extract Zendesk subdomain from the API URL or construct the agent URL
let agentTicketUrl = '';
if (ticketData.url) {
  // Extract subdomain from API URL (e.g., https://softwarecompany-66332.zendesk.com/api/v2/tickets/123.json)
  const urlMatch = ticketData.url.match(/https:\/\/(.*?)\.zendesk\.com/);
  if (urlMatch) {
    const subdomain = urlMatch[1];
    agentTicketUrl = `https://${subdomain}.zendesk.com/agent/tickets/${ticketData.id}`;
  } else {
    // Fallback: construct from ticket ID (replace 'your-subdomain' with actual subdomain)
    agentTicketUrl = `https://softwarecompany-66332.zendesk.com/agent/tickets/${ticketData.id}`;
  }
} else {
  // Fallback if no URL is provided
  agentTicketUrl = `https://softwarecompany-66332.zendesk.com/agent/tickets/${ticketData.id}`;
}

return {
  ticket_id: ticketData.id,
  ticket_url: agentTicketUrl,
  api_url: ticketData.url, // Keep original API URL for reference
  subject: ticketData.subject,
  requester_name: inputData.requester_name,
  requester_email: inputData.requester_email,
  source_channel: inputData.source,
  original_id: inputData.original_id,
  priority: ticketData.priority,
  status: ticketData.status,
  created_timestamp: new Date().toISOString(),
  zendesk_created_at: ticketData.created_at,
  description_preview: inputData.description.substring(0, 100) + (inputData.description.length > 100 ? '...' : ''),
  tags: ticketData.tags ? ticketData.tags.join(', ') : ''
};

// Normalize data from Gmail
if ($input.first().json.source === undefined) {
  const gmailData = $input.first().json;

  // Extract email and name from the 'from' object structure
  let requesterEmail = '';
  let requesterName = '';

  if (gmailData.from && gmailData.from.value && gmailData.from.value[0]) {
    requesterEmail = gmailData.from.value[0].address || '';
    requesterName = gmailData.from.value[0].name || gmailData.from.value[0].address || '';
  }

  // Use plain text first, fallback to textAsHtml if text not available, avoid html
  let description = '';
  if (gmailData.text) {
    description = gmailData.text;
  } else if (gmailData.textAsHtml) {
    // Strip HTML tags from textAsHtml to get plain text
    description = gmailData.textAsHtml.replace(/<[^>]*>/g, '').replace(/&apos;/g, "'").replace(/&quot;/g, '"').replace(/&lt;/g, '<').replace(/&gt;/g, '>').replace(/&amp;/g, '&');
  } else {
    description = 'No description provided';
  }

  // Check if it's urgent based on subject or content
  const isUrgent = (gmailData.subject && gmailData.subject.toLowerCase().includes('urgent')) || 
                   (description && description.toLowerCase().includes('urgent'));

  return {
    source: 'gmail',
    subject: gmailData.subject || 'No Subject',
    description: description,
    requester_email: requesterEmail,
    requester_name: requesterName,
    priority: isUrgent ? 'urgent' : 'normal',
    timestamp: new Date().toISOString(),
    original_id: gmailData.id,
    raw_data: gmailData
  };
}

return $input.first().json;
