const input = $input.first();
let records = Array.isArray(input?.json?.records)
  ? input.json.records
  : ["deals","companies","tickets"];

return records.map(name => ({ json: { record: name } }));

// Build batch/read requests for only: deals, companies, tickets

const PROPS = {
  deals: [
    "dealname",
    "amount",
    "dealstage",
    "pipeline",
    "closedate",
    "hubspot_owner_id",
    "hs_lastmodifieddate",
  ],
  companies: [
    "name",
    "domain",
    "industry",
    "numberofemployees",
    "annualrevenue",
    "website",
    "phone",
    "city",
    "state",
    "country",
    "hubspot_owner_id",
    "createdate",
    "hs_lastmodifieddate",
  ],
  tickets: [
    "hs_ticket_id",
    "subject",
    "content",
    "hs_pipeline",
    "hs_pipeline_stage",
    "hs_ticket_priority",
    "hs_lastmodifieddate",
    "createdate",
    "closed_date",
  ],
};

// If the upstream node emits these three in order, this helps infer the object when not provided
const ORDER = ["deals", "companies", "tickets"];

function toBatchRead(item, idx) {
  const object = item.json.object || item.json.record || ORDER[idx];

  const results = Array.isArray(item.json.results) ? item.json.results : [];
  const ids = results.map(r => String(r.toObjectId)).filter(Boolean);

  return {
    json: {
      object,
      url: `https://api.hubapi.com/crm/v3/objects/${object}/batch/read`,
      method: "POST",
      headers: { "content-type": "application/json" },
      body: {
        properties: PROPS[object] || [],
        archived: false,
        inputs: ids.map(id => ({ id })),
      },
      hasInputs: ids.length > 0,
      count: ids.length,
    },
  };
}

return $input.all().map(toBatchRead);

// n8n Code node (JavaScript)
// Input: three items (HubSpot batch/read responses) for deals, companies, tickets (order unknown)
// Output: a single consolidated item with cleaned, LLM-ready fields

const items = $input.all().map(i => i.json);

// --- helpers ---
const isNonEmpty = v => v !== null && v !== undefined && v !== '';
const stripNulls = obj =>
  Object.fromEntries(Object.entries(obj).filter(([, v]) => isNonEmpty(v)));

function detectType(block) {
  const first = block?.results?.[0]?.properties || {};
  if ('dealname' in first || 'dealstage' in first) return 'deals';
  if ('hs_ticket_id' in first || 'hs_pipeline' in first) return 'tickets';
  if ('name' in first || 'industry' in first) return 'companies';
  return 'unknown';
}

function mapDeal(p) {
  return stripNulls({
    id: p.hs_object_id || p.id,
    name: p.dealname,
    stage: p.dealstage,
    amount: isNonEmpty(p.amount) ? Number(p.amount) : undefined,
    pipeline: p.pipeline,
    closeDate: p.closedate,
    ownerId: p.hubspot_owner_id,
    createdAt: p.createdate,
    lastUpdatedAt: p.hs_lastmodifieddate,
  });
}

function mapCompany(p) {
  // Derive a simple location string when possible
  const parts = [p.city, p.state, p.country].filter(isNonEmpty);
  const hq = parts.length ? parts.join(', ') : undefined;

  return stripNulls({
    id: p.hs_object_id || p.id,
    name: p.name,
    domain: p.domain,
    website: p.website,
    phone: p.phone,
    industry: p.industry,
    employees: isNonEmpty(p.numberofemployees) ? Number(p.numberofemployees) : undefined,
    annualRevenue: isNonEmpty(p.annualrevenue) ? Number(p.annualrevenue) : undefined,
    headquarters: hq,
    ownerId: p.hubspot_owner_id,
    createdAt: p.createdate,
    lastUpdatedAt: p.hs_lastmodifieddate,
  });
}

function mapTicket(p) {
  return stripNulls({
    id: p.hs_ticket_id || p.hs_object_id || p.id,
    subject: p.subject,
    description: p.content,
    pipelineId: p.hs_pipeline,
    stageId: p.hs_pipeline_stage,
    priority: p.hs_ticket_priority,
    createdAt: p.createdate,
    lastUpdatedAt: p.hs_lastmodifieddate,
    closedDate: p.closed_date,
  });
}

// --- collect ---
const out = { deals: [], companies: [], tickets: [] };

for (const block of items) {
  const t = detectType(block);
  const rows = Array.isArray(block.results) ? block.results : [];
  if (t === 'deals') {
    out.deals = rows.map(r => mapDeal(r.properties || {})).filter(o => Object.keys(o).length);
  } else if (t === 'companies') {
    out.companies = rows.map(r => mapCompany(r.properties || {})).filter(o => Object.keys(o).length);
  } else if (t === 'tickets') {
    out.tickets = rows.map(r => mapTicket(r.properties || {})).filter(o => Object.keys(o).length);
  }
}

// Optional high-level summary for the LLM
out.summary = {
  dealCount: out.deals.length,
  companyCount: out.companies.length,
  ticketCount: out.tickets.length,
};

// Emit a single consolidated item
return [{ json: out }];

