const potentialSlots = [];

const config = $('1. CONFIGURATION (EDIT ME)').item.json;

const initialISO = $('Webhook: Production URL = VAPI Server URL').item.json.body.message.toolCalls[0].function.arguments.initialSearchDateTime;

const datePart = initialISO.split('T')[0];
const offsetPart = initialISO.slice(-6);
const startHourString = String(config.workdayStartHour).padStart(2, '0');
const endHourString = String(config.workdayEndHour).padStart(2, '0');

const workdayStartISO = `${datePart}T${startHourString}:00:00${offsetPart}`;
const workdayEndISO = `${datePart}T${endHourString}:00:00${offsetPart}`;

let currentSlot = new Date(workdayStartISO);
const endOfWorkday = new Date(workdayEndISO);

while (currentSlot.getTime() < endOfWorkday.getTime()) {
  const slotEnd = new Date(currentSlot.getTime() + config.bookingCadenceMinutes * 60000);
  potentialSlots.push({
    start: currentSlot.toISOString(),
    end: slotEnd.toISOString(),
  });
  currentSlot = slotEnd;
}

return [{
  json: {
    ...config, 
    potentialSlots: potentialSlots
  }
}];

const config = $('Calculate Potential Slots (do not change)').item.json;
const potentialSlots = config.potentialSlots;
const busyEvents = $('2. Get Calendar Events (EDIT ME)').all();
const toolCallId = $('Webhook: Production URL = VAPI Server URL').first().json.body.message.toolCalls[0].id;

const busyIntervals = busyEvents
  .filter(event => event.json.start && event.json.start.dateTime) 
  .map(event => {
    const start = new Date(event.json.start.dateTime);
    const end = new Date(event.json.end.dateTime);
    const startWithBuffer = new Date(start.getTime() - config.bufferBeforeMinutes * 60000);
    const endWithBuffer = new Date(end.getTime() + config.bufferAfterMinutes * 60000);
    return { start: startWithBuffer, end: endWithBuffer };
  });

const availableSlots = potentialSlots.filter(slot => {
  const meetingStart = new Date(slot.start); 
  const meetingEnd = new Date(meetingStart.getTime() + config.meetingDurationMinutes * 60000);
  if (meetingStart < new Date()) { return false; }
  const isOverlapping = busyIntervals.some(busy => (meetingStart < busy.end) && (meetingEnd > busy.start));
  return !isOverlapping;
});

const formattedSlots = availableSlots.map(slot => {
  const utcDate = new Date(slot.start); 

  const humanReadable = utcDate.toLocaleString('en-US', {
    timeZone: config.timeZone,
    weekday: 'long', month: 'long', day: 'numeric',
    hour: 'numeric', minute: '2-digit', hour12: true,
  });


  const localFormatter = new Intl.DateTimeFormat('en-US', {
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hourCycle: 'h23', 
    timeZone: config.timeZone
  });

  const localParts = localFormatter.formatToParts(utcDate);
  let localYear, localMonth, localDay, localHour, localMinute, localSecond;

  for (const part of localParts) {
    switch (part.type) {
      case 'year': localYear = part.value; break;
      case 'month': localMonth = part.value; break;
      case 'day': localDay = part.value; break;
      case 'hour': localHour = part.value; break;
      case 'minute': localMinute = part.value; break;
      case 'second': localSecond = part.value; break;
    }
  }

  const fakeUtcDateFromLocal = new Date(
    `${localYear}-${localMonth}-${localDay}T${localHour}:${localMinute}:${localSecond}.000Z`
  );

  const offsetMillis = fakeUtcDateFromLocal.getTime() - utcDate.getTime();
  const offsetMinutes = offsetMillis / 60000; 

  const absOffsetMinutes = Math.abs(offsetMinutes);
  const offsetHours = Math.floor(absOffsetMinutes / 60);
  const remainingOffsetMinutes = absOffsetMinutes % 60;

  const offsetSign = offsetMinutes < 0 ? '-' : '+';

  const isoOffset = `${offsetSign}${String(offsetHours).padStart(2, '0')}:${String(remainingOffsetMinutes).padStart(2, '0')}`;

  const isoDateTime = `${localYear}-${localMonth}-${localDay}T${localHour}:${localMinute}:${localSecond}${isoOffset}`;

  return { humanReadable, isoDateTime };
});

return [{
  json: {
    toolCallId: toolCallId,
    availableSlots: formattedSlots
  }
}];

