// Quick fix to extract the actual response from OpenAI Assistant
const response = $json;

// Log the full response to debug
console.log("Full OpenAI Response:", JSON.stringify(response, null, 2));

// Extract the actual message content
let actualResponse = '';

// Try different possible locations for the response
if (response.data && response.data.content && response.data.content[0] && response.data.content[0].text) {
  actualResponse = response.data.content[0].text.value;
} else if (response.content && response.content[0] && response.content[0].text) {
  actualResponse = response.content[0].text.value;
} else if (response.output) {
  actualResponse = response.output;
} else if (typeof response === 'string' && !response.startsWith('thread_')) {
  actualResponse = response;
}

return {
  chatResponse: actualResponse,
  threadId: response.thread_id || response.threadId,
  originalData: response
};
