const llmOutput = $json.text;
let cleanJsonString = llmOutput; // Start with the full output

// 1. Try to extract content from a full markdown JSON block (```json...```)
const fullMarkdownMatch = llmOutput.match(/```json\s*([\s\S]*?)\s*```/);
if (fullMarkdownMatch && fullMarkdownMatch[1]) {
    cleanJsonString = fullMarkdownMatch[1];
} else {
    // 2. If no full markdown block, check for only trailing ``` (your current problem)
    //    Remove any trailing ``` and surrounding whitespace
    const trailingBackticksMatch = llmOutput.match(/^(.*?)\s*`{3,}\s*$/s); // `s` flag allows . to match newlines
    if (trailingBackticksMatch && trailingBackticksMatch[1]) {
        cleanJsonString = trailingBackticksMatch[1];
    } else {
        // 3. Fallback: No markdown, assume the entire output *should* be JSON.
        //    (cleanJsonString already holds llmOutput, so no change needed here)
        //    We could also try to trim whitespace just in case.
        cleanJsonString = llmOutput.trim();
    }
}


// Now attempt to parse the cleaned string
try {
    const parsedJson = JSON.parse(cleanJsonString);
    return [{ json: parsedJson }];
} catch (e) {
    console.error("JSON Parsing Error in Code Node:", e.message);
    console.error("String attempted to parse:", cleanJsonString);
    throw new Error(
        "Failed to parse JSON from LLM output: " + e.message +
        "\nString attempted to parse:\n" + cleanJsonString +
        "\nRaw LLM Output was:\n" + llmOutput
    );
}

